@-moz-document url-prefix("http://www.roblox.com"), url-prefix("https://www.roblox.com"), url-prefix("https://web.roblox.com"), url-prefix("http://web.roblox.com") {
#MasterContainer{background:url(http://i.imgur.com/StCcUtE.png) repeat #F0F0F0!important}
#MasterContainer > div > div.SystemAlert{background-color:rgba(0,0,0,0)!important}
#ctl00_SystemAlertDiv{background-color:rgba(0,0,0,0)!important}
#Footer.footer-container{background:#DFDFDF!important}
#Footer.footer-container .legal{border-top:2px solid #000}
#Footer.footer-container .Legalese,#Footer.footer-container a,#Footer.footer-container .FooterNav,#header-or{color:#000!important}
#FavoritesContent .PriceInRobux,#UserContainer .PriceInRobux{color:#02B757!important}
#FavoritesContent .PriceInTickets,#UserContainer .PriceInTickets{color:#CC9E71!important}
#build-page td.menu-area a.tab-item{font-weight:lighter}
.rbx-btn-primary,.rbx-btn-primary-lg,.rbx-btn-primary-md,.rbx-btn-primary-sm,.rbx-btn-primary-xs{background-color:#8B8B8B!important;border-color:#909090!important}
.rbx-btn-secondary,.rbx-btn-secondary-lg,.rbx-btn-secondary-md,.rbx-btn-secondary-sm,.rbx-btn-secondary-xs{border-color:#8B8B8B!important}
.rbx-btn-secondary:hover,.rbx-btn-secondary-lg:hover,.rbx-btn-secondary-md:hover,.rbx-btn-secondary-sm:hover,.rbx-btn-secondary-xs:hover,.rbx-btn-secondary:focus,.rbx-btn-secondary-lg:focus,.rbx-btn-secondary-md:focus,.rbx-btn-secondary-sm:focus,.rbx-btn-secondary-xs:focus{border-color:#6B6B6B!important}
.rbx-upgrade-now #upgrade-now-button{background-color:#8B8B8B!important}
.rbx-upgrade-now #upgrade-now-button:hover{background:#6B6B6B!important}
.btn-primary,.btn-primary:link,.btn-primary:active,.btn-primary:visited{background-image:none!important}
.btn-primary{background-color:#9E9E9E!important;border-color:#939393!important}
.party-window .title{background-image:linear-gradient(to bottom,#B6B6B6,#919191)!important}
.footer-container{background:#222!important}
.container-fluid{background-color:#B6B6B6!important}
.rbx-highlight{background-color:#6B6B6B!important}
.btn-primary:hover,.btn-primary:focus,.btn-primary:active,.btn-primary.active,.open>.btn-primary.dropdown-toggle{background-color:#838383!important;border-color:#939393!important}
.btn-primary,.btn-primary:link,.btn-primary:active,.btn-primary:visited{background-image:none!important}
.PurchaseButton.btn-medium.btn-primary{background-image:none!important}
.btn-primary{background-color:#B6B6B6!important}
.btn-primary{border-color:#939393!important}
.btn-primary,.btn-primary:link,.btn-primary:active,.btn-primary:visited{background-image:none!important}
.btn-neutral,.btn-neutral:link,.btn-neutral:active,.btn-neutral:visited{background-color:#B6B6B6!important}
.btn-negative,.btn-negative:link,.btn-negative:active,.btn-negative:visited{background-image:none!important}
.btn-neutral,.btn-neutral:link,.btn-neutral:active,.btn-neutral:visited{background-image:none!important}
.btn-neutral,.btn-neutral:link,.btn-neutral:active,.btn-neutral:visited{border-color:#B6B6B6!important}
.btn-control.btn-control-medium{background-image:none!important}
.btn-control.btn-control-small{background-image:none!important}
.rbx-btn-secondary,.rbx-btn-secondary-lg,.rbx-btn-secondary-md,.rbx-btn-secondary-sm,.rbx-btn-secondary-xs{background-color:#9E9E9E}
.rbx-btn-secondary:hover,.rbx-btn-secondary-lg:hover,.rbx-btn-secondary-md:hover,.rbx-btn-secondary-sm:hover,.rbx-btn-secondary-xs:hover,.rbx-btn-secondary:focus,.rbx-btn-secondary-lg:focus,.rbx-btn-secondary-md:focus,.rbx-btn-secondary-sm:focus,.rbx-btn-secondary-xs:focus{background-color:#777}
.CatalogItemInner .robux-price{color:#009847!important}
.robux-text{color:#02B757!important;font-weight:lighter!important}
.deemphasized{color:#4C4C4C!important;font-weight:bolder!important}
.CatalogItemInner span.NotAPrice{color:#8E8E8E;font-size:16px;font-weight:lighter}
.redesign .tab_white_31h_container .ajax__tab_inner,.tab,.tab-container>div{font-weight:lighter}
.accord-header{background:#F0F0F0}
.accord-header:hover{background:#E8E8E8}
.verticaltab a{font-weight:lighter}
.verticaltab a{font-size:15px}
.verticaltab a{padding-left:6px}
.SystemAlert a{color:#0055B3}
.SystemAlert a{font-weight:bolder}
div#catalog div#dropdown ul li a,div#catalog div#dropdown ul li span{font-size:17px}
div#catalog #SortOptions{font-weight:lighter;font-size:20px}
div#catalog .breadCrumbFilter{font-size:40px}
div#catalog .bolded{font-weight:lighter;text-transform:none}
div.SystemAlert{border:none}
span.tickets,div.tickets{background-image:url(http://i.imgur.com/VdBmz2x.png)!important;color:#CC9E71!important}
span.robux,div.robux{background-image:url(http://i.imgur.com/7Pidv47.png)!important;color:#02B757!important}
body .forum-table-header,body .forum-table-header th,body .forum-table-footer{background-color:#B6B6B6!important}
body, .text, pre{line-height:1.4}
h2,h2 a:link,h2 a:visited,h2 a:active,h2 .text{font-weight:lighter!important}
div.SystemAlert{color:#666666}
}
