css:
@-moz-document url("http://webopoly.org/game.php"), url("http://webopoly.org/chat.php") {
body { 
    background-color: #1a1a1a;
    color: white;
}
td.topbar, td.sidebar {
	background-color: #2c2c2c;
}

.b_corner, .b_ispfee, .b_sonet, .b_shop, .b_browser, .b_fileshr {
    background-color: white;
}

input[type=button] {
    font:normal 18px sans-serif;
    padding:2px 12px 3px 12px;
    cursor:pointer;
    position:relative;
    overflow:hidden;
    vertical-align:middle;
    margin:0 8px;
    color:#92c564;
    border:1px solid #111;
    background:#2e2f31;
    background:-moz-linear-gradient(top, #45494d, #161616);
    background:-webkit-gradient(linear, left top, left bottom, from(#45494d), to(#161616));
    text-decoration:none;
    -moz-border-radius:4px;
    -webkit-border-radius:4px;
    -moz-box-shadow:rgba(0,0,0,.4) 0 0 8px;
    -webkit-box-shadow:rgba(0,0,0,.4) 0 0 8px;
    text-shadow:rgba(255,255,255,.6) 0 0 16px;
    -moz-transition:all .2s linear;
    -webkit-transition:all .2s linear;
font-size: 12px !important;
}

input[type=button]:disabled {
    color: #808080;
}

input[type=button]:hover {
	color:#c5e4a9;
	text-shadow:rgba(255,255,255,1) 0 0 12px;
}
input[type=button]:disabled:hover {
    color: #808080;
	text-shadow:rgba(255,255,255,.6) 0 0 16px;
}

a[href] {
	color: white;
}
a[href]:hover {
 	color: cyan;
}
#p_selltrade_outstanding_trades_container_outgoing, #p_selltrade_outstanding_trades_container_incoming {
    background-color: #2c2c2c !important;
}
div[style*="background-color: rgb(170, 255, 221)"] {
    background-color: red;
}
#p_tabs div {
	background-color: #2c2c2c !important; 
}
.b_player {
	color: black;
}
}
