css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("winbeta.org") {
* {font-family:Segoe UI !important;}
body {background-color:white !important;}  
.logowin, #logo > a:nth-child(1)  {
  font-family:Segoe UI Light !important;
  font-weight:normal !important;
}
#logo {
  background-image:url("http://upload.wikimedia.org/wikipedia/commons/thumb/3/34/Windows_logo_-_2012_derivative.svg/55px-Windows_logo_-_2012_derivative.svg.png") !important;
  background-size:32px 32px !important; 
  background-position: 4px 10px !important;
  background-repeat:no-repeat !important; 
}  
.ultimenu-item, .topiclinks  {
  font-weight:normal !important;
}
.hasultimenu, .ultimenu-item  {
  background-color:white !important; 
}
.hasultimenu, .ultimenu-item {
  border: 1px solid white !important;
  border-bottom: 0px solid white !important; 
}  
.hasultimenu:hover, .ultimenu-item:hover {
  background-color:#E5E5E5 !important; 
  color:#444444 !important; 
  border:1px solid #888888 !important;
  border-bottom: 0px solid white !important; 
}  
*  {transition: 0.0s ease 0s, color 0.0s ease 0s !important;}
.followtip, .searchbit, .sprite-tw, .sprite-yt, .sprite-fb, .sprite-gp, .sprite-rss  {cursor:text !important;}  
.follow {margin-right:0px !important;}
.count {display:none !important;}  
#tagcontainer {
  /*background-color:rgb(4,116,179)*/
  background-color:white !important;
  border:1px solid #888888 !important;
}  
.topiclinks {
  color:white !important;
} 
.topiclinks:hover {
  color:#444444 !important;
  /*background-color:rgb(28,131,187) !important;*/
  background-color:#E5E5E5 !important;
}  
.ultimenu-flyout .region {
/*  background-color:rgb(4,116,179) !important;*/
  background-color:white !important;
/*  border:1px solid white !important;*/
/*  border-top:0px solid white !important; */
  box-shadow:none !important; 
}    
.has-ultimenu {
  width:152px !important;
  text-align:center !important;
}  
.block-views, .ultimenu-basic .block {
  margin:0px !important;
  padding-bottom:0px !important;
}  
.stream {
/*  background-color:rgb(4,116,179) !important;                 */
/* color: white !important;*/  
  background-color: white !important;  
  color: #444444 !important;
  padding: 0px !important;
  margin:0px !important;
  height:auto !important;
  width:100% !important;
}   
.stream:hover {background-color:#E5E5E5 !important;}        
.ultimenu-flyout {
  width:152px !important;
/*  border:0px solid rgb(4,116,179) !important;*/
/*  border:1px solid #888888 !important;*/
  border-top:0px solid white !important; 
/*  box-shadow: 0px 0px 10px #888888 !important;*/
}  
.region-ultimenu-ultimatemenu-forum, .region-ultimenu-ultimatemenu-features, .region-ultimenu-ultimatemenu-reviews, .region-ultimenu-ultimatemenu-news {
  border-left:1px solid #888888 !important;
  border-right:1px solid #888888 !important;
  border-bottom:1px solid #888888 !important;
}  
.ultimenu-ultimatemenu-forum {margin-right:31.6%} 
.ultimenu-ultimatemenu-features {margin-left:42.05%}   
.ultimenu-ultimatemenu-reviews {margin-left:28.7%}   
.ultimenu-ultimatemenu-news {margin-left:15.4%}     
  
.ultimenu-flyout .view-header {display:none !important;}  
.streamtitle a {color:#444444 !important;} 
.commentfloat {display:none !important;}         
.streamimg {display:none !important;}  
li.has-ultimenu > a:after {display:none !important;}    
.view-ultiblock-content h3 {color:white !important;}    
#mobilemenu {
  background-color:rgb(4,116,179) !important;
  margin-top:40px !important;
}  
#mobilemenu li:hover {
  background-color:rgb(28,131,187) !important;
}     

.herodiv {
display:none !important;
}          
#mobilemenu > hr {
  color:#888888 !important;
  border-color:#888888 !important;
  background-color:#888888 !important; 
  height:1px !important;
}  
.backtotop {display:none !important;}  
.storeapp {background-color:rgb(43,121,178) !important;}   
.feedlysubscribe {font-weight:normal !important;}      
#pn {background-color:white !important;}  
.navquot {font-weight:normal !important;}     
#footer {
  background-color:white !important;
  border-top: 1px solid grey !important;
  border-bottom-width:0px !important;
}    
#togglenav, #togglenav::before, #togglenav::after {
  background-color:#444444 !important;
  height:1.5px !important;
}
.topiclinks {
  color: #444444 !important;
  padding:9px 12px !important; 
}  
#mobilemenu {
  background-color:white !important;
  border:1px solid #888888 !important;
} 
#mobilemenu a {
  color:#444444 !important;
}  
#mobilemenu a:hover {
  background-color:#E5E5E5 !important;
}  
.view-header, #block-block-11 > h3:nth-child(1) {
  background-color:white !important;
  color:#444444 !important;
  border:1px solid #888888 !important;
  border-bottom:none !important; 
  font-weight:normal !important;
  text-transform:capitalize !important;
  padding-bottom:8px !important;
}
#block-views-weeklytrendingstories-block .view-content, #block-views-weeklytrendingstories-block-2 .view-content, #block-views-weeklytrendingstories-block-1--2 .view-content, #block-views-weeklytrendingstories-block-2--2 .view-content, #block-block-11 .content, #block-block-11--2 .content, #block-follow .view-content {
    border-top: 1px solid #888888 !important;
}
.view-weeklytrendingstories, .view-weeklytrendingstories .view-header {
  border: 0px solid #888888 !important;
  border-bottom:1px solid #888888 !important; 
}  
.view-content .stream {
/*  border-left:1px solid #888888 !important;*/
/*  border-right:1px solid #888888 !important;*/
  cursor:pointer !important;
}  
.view-weeklytrendingstories > div:nth-child(2) > div:nth-child(4)  {
  border-bottom: 1px solid #888888 !important; 
}
* {box-shadow:none !important;}
  
}
