css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("korben.info") {

.topbar, .subnav, .side, .footer, .edito, .edito-footer, .navigation, .univers, .ribbon, .baby-korben, .similar-posts, .information, .post-share, #us_report_button, .post-subscribe, .post-landing-register, .post-social-plugins, center {
    display: none;
}

.gotop {
    opacity:0.5;
}

.gotop:hover {
    opacity:0.8;
}


#disqus_thread {
    width: 98%;
    margin: auto;
    background: white !important;
}

.post-content, .post-text, .post-thumbnail, .wrap {
    width: auto !important;
}

.post-content {
    padding: 0 15px;
}

.wrap {
    padding: 0 7% !important;
}

.maincontent, .main {
    width: 100% !important;
    padding: 0;
}

.maincontent {
    margin: 15px 0 !important;
}

.post-thumbnail {
    height: auto !important;
    margin: 20px 0 !important;
}

.top {
    background: #243454 !important;
    border-bottom: solid 2px #243454;
}

body {
    background: #e9eaed !important;
}

a, .orange {
    color: #3b5998 !important;
}

a:hover {
    color: #3a5795 !important;
}

.header {
    width: 100%;
    height: 45px;
}

.logo {
    height: 30px !important;
    margin: 7px 10px !important;
    padding: 0 !important;
}

.header img {
    height: 30px;
    width: auto;
}

.post-thumbnail img, .post-image img {
    width: 650px;
    height: auto !important;
    margin-left: calc(50% - 325px);
}

.main {
    border-top: #000 0 solid !important;
}

.top {
    background: none;
}

body {
    background: #e9eaed !important;
}

a, .orange {
    color: #3b5998 !important;
}

a:hover {
    color: #3a5795 !important;
}

.logo {
    height: 40px !important;
    margin: 7px 20px !important;
    padding: 0 !important;
}

.header img {
    height: 40px;
    width: auto;
}

::-moz-selection {
    background: lightsteelblue !important;
    color: black !important;
}

::selection {
    background: lightsteelblue !important;
    color: black !important;
}

.post-title {
    margin-top: 10px !important;
    margin-left: 5px !important;
}

.post-text img {
    display: block !important;
    margin: auto;
}

.current {
    color: black !important;
}

.post-date {
    margin-right: 7px;
}

.wp-pagenavi {
    margin: 0 10px;
}

@media screen and (max-width: 650px) {
    .wrap {
        padding: 0 10px !important;
    }
}

@media screen and (min-width: 650px) and (max-width: 720px) {
    .wrap {
        padding: 0 3% !important;
    }
}

@media screen and (min-width: 720px) and (max-width: 820px) {
    .wrap {
        padding: 0 5% !important;
    }
}

@media screen and (max-width: 760px) {
    .post-image img, .post-thumbnail img {
        width: 100% !important;
        margin-left: 0 !important;
    }

    .post-text img, .post-content img {
        width: 100% !important;
        height: auto;
        max-width: 100%;
    }

    .post-text iframe, .post-content iframe {
        width: 100% !important;
    }
}
}
