css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.fantabobshow.com") {
  
/*
  ############################################################################################################
  #Si certaines parties du CSS ne modifient ni les couleurs ni les images, laissez-les et passez votre chemin#
  ############################################################################################################
  #29/12 : Parce que je préfère comme ça, les trucs modifiables sans risque d'horrible catastrophe sont      #
  #mtn à la première ligne de chaque élément.                                                                #
  #L'erreur pour la ligne d'accueil/haut est à ignorer, je sais pas pourquoi elle est là mais elle marche qdm#
  ############################################################################################################
*/
  
/*Bannière*/
#header
  {background: rgb(46, 98, 176) url(https://dl.dropboxusercontent.com/u/47007147/FBS2/Banniere.png) repeat-x bottom} 
  
#logo img /*Masquer le logo*/
  {display:none}
  
/*Barre nav*/
.secondaryBar /*Marquer tous les forums comme lus etc*/
  {height: 43px;background: #1B1B1B}
  
.navTabs .navTab.selected .navLink /*Accueil/Forum/Shoutbox etc*/
  {background: #1B1B1B scroll center bottom;padding-top: 0px;margin-top: 0px}
  
/*Fond et pied de page*/
#content /*Fond en général*/
  {background: #e0e0e0}
  
.footer /*Partie du bas*/
  {background: #e0e0e0}
  
.breadcrumb .jumpMenuTrigger /*Flèche vers le bas sur le bout des barres*/
  {background: transparent url(https://dl.dropboxusercontent.com/u/47007147/FBS2/circle-arrow-down.png) no-repeat;width: 23px;height: 15px}
  
.breadcrumb .crust .arrow span /*Flèche vers le côté dans les barres*/
  {background-image: url(https://dl.dropboxusercontent.com/u/47007147/FBS2/breadcrumb-arrow.png);width: 7px}
  
.titleBar h1
  {display: none}
  
.breadcrumb
  {margin-top: 0px}
  
.footer .choosers /*Masquer le sélecteur de style intégré (inutilisable)*/
  {display: none}
  
#copyright a:link, #copyright /*Virer le copyright*/
  {display: none}
  
.legal span /*Virer les infos légales*/
  {display: none}
  
/*Forum en lui-même*/
.breadBoxTop /*Barre du haut Accueil>Forum, ne pas changer la hauteur inconsciemment*/
  {background: #1B1B1B;
  height: 37px}
  
.breadBoxBottom /*Barre du bas Accueil>Forum*/
  {background: #1B1B1B}
  
.nodeList .categoryStrip /*Fond des titres des sections*/
  {background: #1B1B1B}
 
.mainContent /*Fond du forum*/
  {background: white}
  
/*Liste des sujets*/
.discussionList .sectionHeaders /*Barre titre/date de début etc*/
  {background: #1B1B1B}
  
a.callToAction span /*Bouton Poster une nouvelle discussion - Ne pas changer l'url bosser avec le reste*/
  {background: #1B1B1B url(http://forum.fantabobshow.com/styles/minecraft/xenforo/gradients/form-button-white-25px.png) repeat-x center -8px}
  
a.callToAction /*pareil*/
  {border: 1px solid #22826a;
  background: #1B1B1B url(http://forum.fantabobshow.com/styles/minecraft/xenforo/gradients/form-button-white-25px.png) repeat-x center top;margin-right: 10px}
  
#content .pageContent /*Fond du forum*/
  {background: white}
  
/*Overlay*/
.xenPreviewTooltip /*Fond du bloc de prévisualisation du sujet, de base transparent (rendant illisible), préférez un fond clair, j'ai gardé le même que d'habitude*/
  {background: white}
  
.subHeading /*J'ai oublié. Le Highlight habituel*/
  {background: white}
  
.memberListItem /*Parei avec le content-bg*/
  {background: white}

/*Sidebar*/  
.sidebar .section .primaryContent   h3, .sidebar .section .secondaryContent h3 /*Barre des blocs du côté*/
  {background-image: url(http://i.imgur.com/CQKUg6j.gif);
    background-position: bottom;margin: 16px -15px 0;background-repeat: repeat;padding-top: 10px}
  
.sidebar .primaryContent, .sidebar .secondaryContent /*Fond des blocs du côté de l'index*/
  {background: white}
  
/*Search*/
.formPopup .controlsWrapper /*Ne pas toucher*/
  {background: white url(http://forum.fantabobshow.com/styles/minecraft/xenforo/gradients/category-23px-light.png) repeat-x top}
  
.button.primary /*Bouton Rechercher, je sais pas pq j'ai mis la couleur comme ça*/
  {/*background-color: rgb(2, 40, 38)*/}
    
/*Couleurs*/
/*J'ai oublié l'utilité de pas mal, fiez vous aux teintes pour mettre les votres*/
/*#124c3e foncé  #22826a clair  #195f4e moyen*/
.primaryContent a /*Lien tel qu'on le trouve dans l'index en titre de section*/
  {color: #a26565}
  
.secondaryContent a /*Lien tel qu'on le trouve dans l'index en dernier message (à droite des titres)*/
  {color: #ebe8e1}
  
a:link, a:visited /*Liens visités*/
  {color: #a26565}
  
.sidebar .section .secondaryContent .footnote, .sidebar .section .secondaryContent .minorHeading
  {color: #5a3737}
  
#userBar .Popup .PopupControl.PopupOpen a, #userBar .Popup.PopupContainerControl.PopupOpen a
  {color: #a26565}

#userBar a, #userBar .itemCount /*Ne pas toucher*/
  {color: white}
  
.Menu
  {border-color: #ebe8e1}
    
.Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen
  {background: #ebe8e1 url(http://forum.fantabobshow.com/styles/minecraft/xenforo/gradients/user-bar-hover.png) repeat-x top}
  
.Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen
  {background:  #ebe8e1 url(http://forum.fantabobshow.com/styles/minecraft/xenforo/gradients/user-bar-hover.png) repeat-x scroll center top}

.blockLinksList a, .blockLinksList label /*Menu déroulant User/Conversations/etc*/
  {color: #a26565}
  
.textWithCount.subHeading .text /*Profil de membre, "Je suis fan";"Mes fans"*/
  {color: white}
  
.heading, .xenForm .formHeader /*Menu Mon compte*/
  {background-color: #2f2f2f}
    
.footer a, .footer a:visited /*Y'a une erreur que je comprends pas, mais ça marche, bouton accueil et haut*/
  {color: white}
  
.sectionFooter
  {background: url(https://dl.dropboxusercontent.com/u/47007147/FBS2/category-23px-light.png) repeat-x scroll center top #bbbbbb}

/*Mobile*/
.node .nodeDescription
 {display: none}
}
