css:
body, .oc-body main {
  background: rgb(245,245,245) !important; 
}
.category-forum {
  background : white !important;
  border : 1px solid rgb(200,200,200);
  border-bottom : 3px solid rgb(200,200,200);
}
.col8 {
  width : auto !important;  
}
.visible-l {
  display : none !important; 
}
.category-forum__bottom {
  display: none !important; /* A ENLEVER SI VOUS VOULEZ VOIR LE DERNIER MESSAGE DE LA SECTION (page d'accueil forum)*/
}

.category-forum__bottom:hover,.category-forum__top:hover{
  background:rgb(245,245,245) !important;
}

.category-forum__top {
  height : 80px !important;
  padding : 9px 9px  0 9px !important;
}
section#mainContent ul.list li{
  margin-bottom : 0 !important; 
  border-bottom : 2px solid #7b7b7b !important;
  
}

section#mainContent ul.list li.lock, section#mainContent ul.list li.read {
  background: none !important;
}

.btn {
  padding : 6px 12px !important;
}
.btn-primary, .btn-primary[disabled] {
  background : #f39539 !important;
  box-shadow : none !important;
  border : none !important;
  border-radius : 0 !important;
  border-bottom :3px solid #d36f0c !important;
  text-shadow : none !important;
}
.span3 {
  width : auto; 
}
section#mainContent ul.list li div.row div.span5, section#mainContent ul.list li:hover div.row div.span5 
{
  width : 40% !important; 
}
.span1 {
  width : 0 !important; 
}

section#mainContent ul.list li div.row div.span3, section#mainContent ul.list li:hover div.row div.span3 {
  margin-left : 40px ! important; 
}
.span2 {
  width : 10% !important; 
}


section#mainContent .comments .row:nth-child(even) {
  background: white !important; 
}
section#mainContent section.comments div.row {
  border-bottom :  2px solid #7b7b7b !important;
}
.row:hover {
  background: normal !important;
}

.main-section.inset, section.inset {
  border : 1px solid rgb(200,200,200) !important;
  border-left: 0 !important;
  border-right: 0 !important;
}

.main-section.inset, section.inset {
  box-shadow : none !important;
}
.mainHeader {
  background : rgb(230,230,230) !important;
  
}

.alt-section, ul.nav-tabs {
  background : none !important; 
}

ul.nav-tabs>li.active {
  background : none;
  font-weight: bold
    
}
ul.nav-tabs>li {
  padding: 0 !important;
  margin: 0  !important;
}
.mainHeader__topLine {
   display: none !important; 
}
  .mainHeader__logo {
     background: url(http://img11.hostingpics.net/pics/206138LogoOpenClassrooms.png) no-repeat;
    }
.mainHeader__content {
   top : 0; 
    border: none;
  }
  .mainHeader__wrapper {
   height : auto !important; 
  }
    .mainHeader__logo {
        width : 90px;
        height: 43px
      }
.mainHeader__content, .mainHeader__topLine { left : 120px !important}
.topSearchBox {
   width : 200px; 
  }
.mainHeader__container--separator::after, .mainHeader__container--separatorLarge::after {
      border : none !important
    }
section#mainContent section#mainSection, .containerSdz .container {
     background: none !important; 
    }

row:hover {
 background : normal !important; 
}
.comments .row {
  background : white !important;
     border : 1px solid rgb(200,200,200) !important;
 	border-bottom : 3px solid rgb(200,200,200) !important;
    
}
section#mainContent section.comments div.row {
 border-bottom : 2px solid rgb(200,200,200) !important;
}
section#mainContent ul.list li {
  border: none !important;
  border: 1px solid rgb(200,200,200) !important;
  border-bottom: none! important;
}
section#mainContent ul.list li:last-child {
  border: none !important;
  border: 1px solid rgb(200,200,200) !important;
  border-bottom: 1px solid rgb(200,200,200) !important;
}
body.oldsdz header#mainHeader section#headerBottom, body.oldsdz header#mainHeader section#headerBottom ul {
 background: none !important; 
}
section#mainContent ul.list li.notRead {
  background: white !important;
  border-left: #f39539 solid 3px !important
}
