css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cporta.iit.bme.hu") {
  
body{
  
 background-color: black;
 font-family: Verdana;
  background-image: url("http://shinnok.com/rants/wp-content/uploads/2011/06/minimalist-wallpaper-black.jpg");
    background-position:center; 
  background-repeat:no-repeat;
  background-attachment: fixed;
  background-size: cover;
}
 #header {
    background-image: url("http://verychard.com/university/cppporta_head.png");
    background-repeat: no-repeat;
    min-height: 60px;
}
  
  #container{
    background-color: #cc0000;
     border: 5px solid black;
    
  }
  
#navbar1 {
    background-color: orange;
    color: white;
    font-weight: bold;
    text-align: center;
}
  
  #menubar li:first-child{
    margin-left:0.5em;
  }
  
  #menubar li {
     display: inline;
    float:none;
    background-color: black;
    color:white;
    text-transform: uppercase;
    font-weight: 600;
    font-size: 12px;
  
  }
  
  #menubar li:hover{
    background-color: #73605D;
  }
  
  #menubar li.active {
  background-color: white;
  }
 
  #menubar li a{
    color:white;
  }
  
  #menubar li.active a{
    color:#D90A2E;
  }
#user-box a {border: 1px solid black;
    color: white;
    padding: 2px 15px;
    text-decoration: none;
    background-color: #e54d02;
}
  h3 a{color: #313131!important;
  }
  
 #user-box a:hover{
   background-color: white;
   color:#e54d02;
 } 
  
  
div.attendance-tbl table{
  margin: 0 auto;
}
  
#help-box a{
  border: 1px solid black; 
  background-color: white;
  font-weight:bold; 
  margin-right: 10px;
  text-decoration: none;
  padding: 2px 10px 2px 10px;
  color:red;
  font-family:Verdana;
}
  
  #help-box a:hover{
    background-color: red;
    color:white;
  }
 
  #content{
    background-color: #1d1e20;
    color: white;
  }
  
  #content a{
    color: red;
    font-style: italic;
    text-decoration: none;
  }
  
  #content a:hover{
    color: #f77105;
  }
  
  #local-login #login-form .submit > input{
    margin: 0;
    width: 11em;
  }
  
  .submit > input {
  width: 40%;
  margin: 0 0 0 30%;
    background-color: orange;
    border: 2px solid black;
    color: white;
    
    text-transform:uppercase;
    font-weight: bold;
}
.submit > input:hover{
  background-color: green;
}
  .submit-files h4 {
    background-color: transparent;
    border: 0px transparent none;
  }

  
  .testcase dd{
    color: black;
  }
  
  .exercise-makeresult pre, .exercise-makeresult-long pre{
    color:black;
  }
  
  
  h4, .CodeMirror{border: 1px solid black;color: black;}


.js .exercise-info #detail:hover,  .submit-files h4:hover{
  color: #f77105;
  cursor: pointer;
}

#footer {
    display:none;
}
  
  .submit-files h4, .exercise-info h3{
    color: red;
  }

  .exercise-file h4{
    color: black;
  }
  
  div#toc {
    color: white;
   padding: 10px;
    border: 3px solid black;
    background-color:rgba(29, 30, 32, 0.7);
  }
  
  div#toc a:hover{
    color: #f77105;
  }
  
div.exercise-status-1 {
    background-color: #9ca0a5;
}
div.exercise-status-2, li.solution-status-progress {
    background-color: #f1f0de !important;
  color: black;
}
div.exercise-status-3, li.solution-status-passed {
    background-color: #9cc390;
}
div.exercise-status-4, li.solution-status-failed {
    background-color: #f46060;
}
  
 div.exercise-status-4  h3 a, li.solution-status-failed h3 a{color:white!important;}
div.exercise-status-5, li.solution-status-received {
    background-color: #fdba57;
}
  
 .solution-status-evaluated > a, solution-status-passed > a, .solution-status-evaluated > a, solution-status-failed > a{
   text-transform: capitalize;
   color: #1d1e20!important;
   padding-left: 10px;
 }
  
   .solution-status-evaluated > a:hover, solution-status-passed > a:hover, .solution-status-evaluated > a:hover, solution-status-failed > a:hover{
   color: white!important;
 }
  
  .solution-status-evaluated span, solution-status-passed span, .solution-status-evaluated span, solution-status-failed span{
    float:right;
    padding-left: 15px;
    font-weight: bold;
  
  }
  
div.attendance-tbl td {
  padding: 10px;
}
  
  
  .exercise-solution-list li{
    padding: 8px;
  }
  
}

@-moz-document url(https://cporta.iit.bme.hu/?attendance) {
h2{
    margin: 10px auto 10px;
    text-align: center;
    width: 400px;
}}
