.tab_notice {
	background:#79D0ED!important;
	border-color:#59BFEB!important;
}

*.tab_notice_value {
	color:#fff!important;
}

/* --- new post buttons above posts --- */

.new_post_label i {color:#222222!important;}

#new_post_label_text > I, #new_post_label_quote > I, #new_post_label_chat > I, #new_post_label_video > I {color:#222222!important;}

.new_post_label/*_icon*/ > I {
    background-image: url('http://static.tumblr.com/dve3anh/Hr1mqzemd/post_icons_sprite.png') !important;
    background-size: auto !important;
    z-index: -1;
  padding-bottom: 5px !important;
    transition-property: background-position !important;
       }
    
  .-new_post_label {
    padding-top: 70px !important;
    padding-bottom: 9px !important;
    /*color: none !important;*/
    /*text-transform: none;*/
    /*font-style: none;*/
    /*text-indent: 0px;*/
  }


.new_post_label > I:before { content: none !important}

.new_post_label:nth-child(1) > I {background-position: 2px -5px !important}
.new_post_label:nth-child(2) > I {background-position: -73px -5px !important}
.new_post_label:nth-child(3) > I {background-position: -148px -5px !important}
.new_post_label:nth-child(4) > I {background-position: -223px -5px !important}
.new_post_label:nth-child(5) > I {background-position: -298px -5px !important}
.new_post_label:nth-child(6) > I {background-position: -373px -5px !important}
.new_post_label:nth-child(7) > I {background-position: -448px -5px !important}

.new_post_label:nth-child(1):hover > I {background-position: 2px -9px !important}
.new_post_label:nth-child(2):hover > I {background-position: -73px -9px !important}
.new_post_label:nth-child(3):hover > I {background-position: -148px -9px !important}
.new_post_label:nth-child(4):hover > I {background-position: -223px -9px !important}
.new_post_label:nth-child(5):hover > I {background-position: -298px -9px !important}
.new_post_label:nth-child(6):hover > I {background-position: -373px -9px !important}
.new_post_label:nth-child(7):hover > I {background-position: -448px -9px !important}

.new_post_label:nth-child(1):active > I {background-position: 2px -5px !important}
.new_post_label:nth-child(2):active > I {background-position: -73px -5px !important}
.new_post_label:nth-child(3):active > I {background-position: -148px -5px !important}
.new_post_label:nth-child(4):active > I {background-position: -223px -5px !important}
.new_post_label:nth-child(5):active > I {background-position: -298px -5px !important}
.new_post_label:nth-child(6):active > I {background-position: -373px -5px !important}
.new_post_label:nth-child(7):active > I {background-position: -448px -5px !important}


  #new_post_label_text {
color:rgb(51,51,51)!important; font-weight:bold; }

    
#new_post_label_photo {
color:rgb(237,79,58)!important; font-weight:bold; }

#new_post_label_quote {
color:rgb(234,134,59)!important; font-weight:bold; }

#new_post_label_link {
color:rgb(232,187,61)!important; font-weight:bold;}

#new_post_label_chat {
color:rgb(197,229,62)!important; font-weight:bold;}

#new_post_label_audio {
color:rgb(64,226,180)!important; font-weight:bold;}

#new_post_label_video {
color:rgb(165,65,224)!important; font-weight:bold;}

.post.new_post .new_post_label{background:none;}


/* --- Post Container --- */

.posts .post_container .post{
  border-radius: 8px !important;
}
      
/* --- Avatar --- */

.post_avatar {
  background-color: transparent !important;
}
.post_avatar_link, .post_sub_avatar {
  -webkit-border-radius:50% !important;
  -moz-border-radius:50% !important;
