}

#dashboard_controls_open_blog * {color:#ededed!important; font-size:10px!important; text-transform:uppercase!important;}

.controls_section *, .controls_section .selected * {background-color:#ededed!important;color:#222222!important; font-size:11px!important; text-transform:uppercase!important;}
}

