css:
@-moz-document url-prefix("http://www.r-bloggers.com/") {
#sidebar {
    display: none;
}
#secondsidebar {
    display: none;
}
.headerimage {
    display: none;
}
#footer {
    display: none;
}
div#mainwrapper {
    background: #222222;
    width: 900px;
    padding-top: none;
    padding-right: none;
}
div#leftcontent {
    background-color: #222222 !important;
    width: 900px;
    margin: 0px;
}
div#leftcontent h2, #leftcontent h3 {
    font-size: 26px;
}
body {
    background: #222222 !important;
}
h1, h2, h3, h4, h5, h6, h1 a, h2 a, h3 a, h4 a, h5 a, body {
    color: #DFDFDF !important;
}
#description.aligncenter {
    float: left;
    font-size: 40px;
    color: #DFDFDF !important;
  	margin: 20px 20px 20px;
}
#header {
    background: #222222 !important;
  	width: 920px;
}
.main-navigation {
    background-color: #828282;
    width: none;
}
.pagination {
    background: #222222 !important;
    border-bottom: none;
    border-right: none;
}
div#twocol {
    width: 900px;
    background: none;
}
#twocol div[id^='post-'] {
    color: #DFDFDF !important;
    width: 100%;
    margin-left: 0%;
    margin-bottom: none;
    float: none;
}
.hentry {
    border-bottom: none;
    margin-bottom: none;
    padding-bottom: none;
}
div#leftcontent .meta {
    float: right;
}
.entry a{
    color: #599AFF;
}	
a{
  color: #599AFF;
}
div#leftcontent .meta{
  color: #888;
}
div[style="border: 1px solid; background: none repeat scroll 0 0 #EDEDED; margin: 1px; font-size: 13px;"] {
	border: none  !important;
	background: none  !important;
}

div[style="border: 1px solid; background: none repeat scroll 0 0 #EDEDED; margin: 1px; font-size: 12px;"]{
	border: none  !important;
	background: none  !important;
}
div[style="border: 1px solid #EB9349; background: none repeat scroll 0 0 #FDEADA; text-align: center; margin: 10px; font-size: 16px;"]{
	border: none  !important;
	background: none  !important;
}
hr{
	border-width: 0px;
}
#leftcontent a.more-link{
  background: #323232;
}
}
