css:
@-moz-document domain(play.pokemonshowdown.com) {
html,body {
    margin: 0;
    padding: 0;
}

body {
    color: white;
    font-size: 11pt;
    font-family: Verdana,Helvetica,Arial,sans-serif;
    background: #344b6c url(http://th05.deviantart.net/fs70/PRE/i/2014/285/1/8/latias___mist_ball_by_ishmam-d5zqy1l.png) no-repeat left center fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
	cursor: url(http://cur.cursors-4u.net/games/gam-13/gam1281.cur), progress !important;
}

button {
background: rgba(0,0,0,0.8);
color: white;
  text-shadow: 1px 1px rgba(0,129,255,0.6);
border: 1px solid rgba(0,129,255,0.85);
border-radius: 3px;
  transition: background 0.5s, border 0.5s, color 0.5s, text-shadow 0.5s, border-radius 0.5s;
  cursor: pointer;
}   

button:hover {
background: rgba(0,129,255,0.8);
color: white;
    text-shadow: 1px 1px rgba(0,0,0,0.6);
border: 1px solid rgba(0,0,0,0.85);
border-radius: 3px;
}

.utilichart .typecol img {
opacity: 0.8;
margin-right: 1px;
}

.pad {
    margin: 0;
    padding: 5px 15px;
}

.label {
    font-size: 9pt;
    font-weight: bold;
    display: block;
}

.optlabel {
    font-size: 9pt;
    display: block;
}

.label strong {
    font-size: 11pt;
    display: block;
}

.label .textbox {
    display: block;
}

.textbox {
    border: 1px solid #AAAAAA;
    border-radius: 3px;
    padding: 2px 3px;
    font-family: Verdana,Helvetica,Arial,sans-serif;
    font-size: 9pt;
    box-shadow: inset 0px 1px 2px #CCCCCC,1px 1px 0 rgba(255,255,255,.6);
    background: #F8FBFD;
    color: black;
}

.textbox:hover {
    border-color: #474747;
    box-shadow: inset 0px 1px 2px #D2D2D2,1px 1px 0 rgba(255,255,255,.6);
    background: #FFFFFF;
}

.textbox:focus {
    outline: 0 none;
    border: 1px solid #004488;
    box-shadow: inset 0px 1px 2px #D2D2D2,0px 0px 5px #2266AA;
    background: #FFFFFF;
}

a.subtle {
    text-decoration: none;
    color: black;
}

a.subtle:hover {
    text-decoration: underline;
    color: #6688CC;
}

.buttonbar {
    margin-top: 1em;
    text-align: center;
}



.header h1 {
    margin: 0;
    padding: 0;
}

.header .logo {
    float: left;
    margin: 2px 0 0 2px;
}

.userbar {
    position: absolute;
    top: 14px;
    right: 14px;
    font-weight: bold;
}

.userbar .username {
    color: black;
    text-shadow: 1px 1px 0 #f8f8f8,1px -1px 0 #f8f8f8,-1px 1px 0 #f8f8f8,-1px -1px 0 #f8f8f8;
}

.username {
cursor: pointer;
text-shadow: 1px 1px 1px rgba(0,0,0,0.2);
}

.userbar button {
    cursor: pointer;
}

.userbar button.icon {
    height: 21px;
    width: 23px;
    font-size: 14px;
    text-align: center;
    padding: 0;
}

.userbar button.icon i {
    margin: 0 -5px;
}

.tabbar {
    position: absolute;
    bottom: 1px;
    left: 0;
    right: 0;
    display: block;
    list-style: none;
    margin: 0;
    padding: 0 0 0 0;
    height: 37px;
    text-align: left;
    color: white;
    text-shadow: 0 1px 0 black;
    font-size: 10pt;
    line-height: 100%;
}

.maintabbarbottom {
    content: "";
    display: block;
    position: absolute;
    left: 0;
    right: 0;
    bottom: -6px;
    height: 6px;
    background: #f8f8f8;
    border: solid 1px #AAAAAA;
    border-left: 0;
    margin: -1px 0 0 0;
    -webkit-box-shadow: 0 1px 2px rgba(0,0,0,.2);
    -moz-box-shadow: 0 1px 2px rgba(0,0,0,.2);
    box-shadow: 0 1px 2px rgba(0,0,0,.2);
}

.tabbar.maintabbar {
    margin-left: 144px;
    margin-right: 165px;
    overflow: hidden;
}

.maintabbar .inner {
    width: 400%;
}

.maintabbar .overflow {
    position: absolute;
    bottom: 0;
    right: 0;
    z-index: 12;
}

.maintabbar .overflow button {
    height: 36px;
    margin: 0;
}

.tabbar li,.tabbar ul {
    display: block;
    float: left;
    list-style: none;
    margin: 0;
    padding: 0;
}

.tabbar a.button {
    outline: none;
    display: inline-block;
    height: 28px;
    min-width: 52px;
    cursor: pointer;
    text-align: center;
    text-decoration: none;
    border-radius: 5px;
    -webkit-box-shadow: 0 1px 2px rgba(0,0,0,.2),inset 0 -1px 2px rgba(255,255,255,1);
    -moz-box-shadow: 0 1px 2px rgba(0,0,0,.2),inset 0 -1px 2px rgba(255,255,255,1);
    box-shadow: 0 1px 2px rgba(0,0,0,.2),inset 0 -1px 2px rgba(255,255,255,1);
    white-space: nowrap;
    color: white;
  	text-shadow: 1px 1px rgba(0,129,255,1.0);
      border: solid 1px rgba(0,129,255,0.8);
  	background: rgba(0,0,0,0.8);
    position: relative;
    padding: 3px 2px 3px 2px;
    margin: 0 -1px 0 0;
    top: 1px;
    border-radius: 0;
    box-shadow: none;
    font-size: 11px;
  	transition: background 0.5s, color 0.5s, text-shadow 0.5s, border 0.5s;
}

.tabbar a.button:hover {
    background: rgba(0,129,255,0.8);
  	color: white;
    text-shadow: 1px 1px rgba(0,0,0,0.8);
  border: solid 1px rgba(0,0,0,0.8);
    z-index: 10;
   
}

.tabbar a.button.cur {
      background: rgba(255,30,30,0.8);  
  	color: white;
  	text-shadow: 1px 1px rgba(0,129,255,1.0);
    border-color: rgba(0,129,255,0.8);
    z-index: 10;
  color: white;
box-shadow: none;
border-bottom: 0;
cursor: url(http://cur.cursors-4u.net/games/gam-13/gam1281.cur), progress !important;
padding-top: 3px;
padding-bottom: 5px;
top: 0px;
}
  
.tabbar a.button.cur:hover {
    color: white;
      text-shadow: 1px 1px rgba(0,0,0,0.8);
    background: rgba(0,129,255,0.8);
    box-shadow: none;
  border: solid 1px rgba(0,0,0,0.8);
    border-bottom: 0;
    cursor: url(http://cur.cursors-4u.net/games/gam-13/gam1281.cur), progress !important;
    padding-top: 3px;
    padding-bottom: 5px;
    top: 0px;
} 

.tabbar a.button i {
    display: block;
    text-align: center;
    margin: 1px auto 0 auto;
    font-style: normal;
    font-size: 14px;
    height: 14px;
}

.tabbar a.button span {
    display: block;
    overflow: hidden;
}

.tabbar a.button i.text {
    font-size: 11px;
    margin-right: 8px;
    padding-left: 0;
    overflow: hidden;
    font-weight: bold;
}

.tabbar a.button i.text+span {
    margin-right: -6px;
}



.tabbar a.button:active {
    background: rgba(90,90,90,0.8);
  text-shadow: 1px 1px 1px rgba(0,129,255,0.8);
}

.tabbar a.button.notifying {
    border-color: rgba(0,0,0,0.8);
    background: rgba(0,129,255,0.8);
  text-shadow: rgba(0,0,0,0.8);
}

.tabbar a.button.notifying:hover {
    border: solid 1px rgba(0,129,255,0.8);
  border-bottom: solid 1px rgba(0,129,255,0.8);
    background: rgba(0,0,0,0.8);
    text-shadow: rgba(0,129,255,0.8);

}

.tabbar li:first-child a.button {
    border-top-left-radius: 5px;
    margin-left: 6px;
}

.tabbar li:last-child a.button {
    border-top-right-radius: 5px;
}

.tabbar a.closable {
    padding-right: 8px;
}

.tabbar .closebutton {
    display: inline-block;
    margin: 0 0 0 -18px;
    width: 18px;
    position: relative;
    z-index: 11;
    top: 5px;
    vertical-align: top;
}

.tabbar a.cur+.closebutton {
    top: 4px;
}

.closebutton, .minimizebutton {
text-decoration: none;
font-size: 14px;
color: #FFFFFF;
text-shadow: 1px 1px 1px rgba(0,0,0,0.4);
border: 0;
padding: 0;
margin: 0;
background: transparent;
cursor: pointer;
  transition: color 0.75s;
}

.closebutton:hover, .minimizebutton:hover {
text-decoration: none;
font-size: 14px;
color: #0081FF;
text-shadow: 1px 1px 1px rgba(0,0,0,0.4);
border: none;
background: transparent;
cursor: pointer;
}

.closebutton:active,.minimizebutton:active {
    color: rgba(100,100,100,1.0);
    text-shadow: 1px 1px 1px rgba(0,129,255,0.3);
}

.ps-room {
    position: absolute;
    display: block;
    top: 56px;
    left: 0;
    right: 0;
    bottom: 0;
    border-left: 1px solid #AAAAAA;
    margin-left: -1px;
}

.scrollable {
    overflow: auto;
    -webkit-overflow-scrolling: touch;
    overflow-scrolling: touch;
}

.ps-room.ps-room-light {
    background: rgba(242,247,250,.85);
    color: black;
}

.ps-room.ps-room-opaque {
    background: #EEF2F5;
    color: black;
}

.ps-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: #CCCCCC;
    background: rgba(212,212,212,.5);
    overflow: auto;
    -webkit-overflow-scrolling: touch;
    overflow-scrolling: touch;
    z-index: 20;
}

.ps-popup {
    position: absolute;
    top: auto;
    right: auto;
    left: auto;
    bottom: auto;
    text-align: left;
    background: #E1E8E8;
    color: black;
    border: 1px solid #999999;
    padding: 2px 10px;
    border-radius: 6px;
    box-shadow: inset 1px 1px 0 #ffffff,inset -1px -1px 0 #DDDDDD,2px 2px 3px rgba(0,0,0,.2);
    z-index: 20;
}

.ps-overlay .ps-popup {
    position: static;
    margin: 80px auto 20px auto;
    max-width: 320px;
}

.ps-popup p,.ps-popup h3 {
    margin: 7px 0;
}

.ps-popup h3 {
    font-size: 12pt;
}

p.or {
    font-size: 12pt;
    font-style: italic;
    font-weight: bold;
    color: #444444;
    margin: 20px 0;
    text-align: center;
}

p.or:before,p.or:after {
    content: "";
    font-size: 10px;
    padding: 0 0 0 85px;
    margin: 0 9px 0 0;
    border-bottom: 1px solid #777777;
    position: relative;
    top: -7px;
}

p.or:after {
    margin: 0 0 0 9px;
}

.ps-popup p.error {
    font-weight: bold;
    color: #EE4422;
}

.popupmenu,popupmenu li {
    margin: -2px 0 -1px 0;
    padding: 0;
    list-style: none;
    font-size: 8pt;
}

.popupmenu h3 {
margin: 5px 0 0 -2px;
padding: 1px 0;
font-size: 8pt;
color: #0081ff;
text-shadow: 1px 1px rgba(0, 0, 0, 0.7);
}

.popupmenu li:first-child h3 {
    margin-top: 0;
}

.popupmenu button {
    display: block;
    cursor: pointer;
    font-size: 8pt;
    font-family: Verdana,Helvetica,Arial,sans-serif;
    margin: 0 0 0 6px;
    padding: 0 4px;
    border: 0;
    border-radius: 2px;
    background: transparent;
    color: black;
    width: 204px;
    height: 19px;
    text-align: left;
    box-sizing: border-box;
  text-shadow: none;
}

.popupmenu button.sel {
    padding: 0 3px;
    border: 1px solid #AAAAAA;
}

.popupmenu button:hover {
    padding: 0 4px;
    border: none;
    background: rgba(255,255,255,0.5);
  text-shadow: 1px 1px rgba(0,129,255,0.6);
}

.popupmenu button.sel:hover {
  padding: 0 3px;
    border: 1px solid #888888;
    background: rgba(255,255,255,0.4);
  text-shadow: 1px 1px rgba(0,129,255,0.6);
 }
    
.select {
    cursor: pointer;
    box-sizing: border-box;
    width: 230px;
    height: 32px;
    padding: 0 5px;
    font-size: 9pt;
    text-align: left;
    font-family: Verdana,Helvetica,Arial,sans-serif;
    border-radius: 4px;
    border: 1px solid rgba(0,129,255,1.0);
    background: linear-gradient(rgba(0,0,0,0.9), rgba(30,30,30,0.9));
    box-shadow: inset 0 1px 1px rgba(255,255,255,1.0);
    color: white;
  text-shadow: 1px 1px rgba(0,129,255,0.8);
  transition: border 0s;
}

.select:hover {
    border-color: rgba(0,0,0,0.9);
    background: linear-gradient(rgba(0,129,255,0.9), rgba(0,60,230,0.9));
  text-shadow: 1px 1px rgba(0,0,0,0.8);
}

.select.teamselect {
    height: 43px;
}

.select:active {
    border: solid 1px rgba(0,0,0,0.9);
    background: rgba(0,60,200,0.9);
    box-shadow: inset 0 1px 1px rgba(255,255,255,1.0);
    
}

.select:after {
    float: right;
    margin-top: 1px;
    font-size: 14px;
    content: "\f0d7";
    font-family: FontAwesome;
    -webkit-font-smoothing: antialiased;
    color: white;
}

.select:disabled {
    border-color: #999999;
    background: rgba(220,220,220,.65);
    color: #555555;
    box-shadow: none;
  text-shadow: none;
}

.select:disabled:after {
    content: "";
}

.select.preselected,.select:disabled.preselected {
    color: black;
}

.leftmenu {
    padding: 40px 0 0 0;
    width: 324px;
}

.rightmenu {
    position: absolute;
    top: 40px;
    right: 25px;
}

.activitymenu {
    position: absolute;
    left: 325px;
    top: 40px;
}

.mainmenu {
    padding: 0 0 30px 0;
}

.battleform {
    width: 230px;
    text-align: left;
    margin: 0 auto;
}

.battleform .buttonbar {
    text-align: center;
}

.mainmenuwrapper {
    position: relative;
    min-height: 100%;
}

.mainmenufooter {
    position: absolute;
    bottom: 15px;
    left: 20px;
    color: rgb(0,129,255);
    text-shadow: 0 0 1px black,0 0 1px black,0 0 1px black;
}

.mainmenufooter blockquote {
    margin: 0 0 1em 0;
    padding: 0;
}

.mainmenufooter small {
    font-size: 9pt;
}

.mainmenufooter small:after {
    content: "Created by xVeNoMiiZz";
      font-size: 9pt;
      position: absolute;
      left: 1px;
      top: -25px;
      color: black;
      text-shadow: 1px 1px 1px rgba(255,255,255,0.4), -1px -1px 1px rgba(255,255,255,0.4), 1px 0 1px rgba(255,255,255,0.4), -1px 0 1px rgba(255,255,255,0.4);
}

.mainmenufooter a {
    text-decoration: underline;
    color: rgb(255,30,30);
  text-shadow: 1px 1px rgba(0,0,0,0.8);
}

.mainmenufooter a:hover {
    text-decoration: underline;
    color: rgb(0,0,0);
  text-shadow: 1px 1px rgba(255,30,30,0.8);
}

.pmbox {
    width: 270px;
    margin: 0 auto;
    min-height: 2px;
}

.pm-window {
    margin-bottom: 12px;
}

.pm-window h3 {
background: rgba(0,129,255, 0.8);
color: black;
margin: 0;
padding: 2px 5px;
font-size: 11pt;
border: 1px solid black;
border-top-left-radius: 5px;
border-top-right-radius: 5px;
cursor: pointer;
  transition: background 0.4s;
}

.pm-window h3:hover {
background: rgba(255,30,30, 0.8);
color: black;
margin: 0;
padding: 2px 5px;
font-size: 11pt;
border: 1px solid black;
border-top-left-radius: 5px;
border-top-right-radius: 5px;
cursor: pointer;
}

.pm-window h3.pm-minimized {
    border-radius: 5px;
}

.pm-window h3.pm-notifying {
    border-color: rgba(255,30,30,0.9);
    background: rgba(0,0,0,0.8);
}

.pm-window h3.pm-notifying:hover {
    border-color: rgba(255,0,0,0.9);
    background: rgba(0,129,255,0.8);
}

.pm-window h3 .closebutton,.pm-window h3 .minimizebutton {
    float: right;
    padding-right: 1px;
}

.pm-window h3 small {
color: #131313;
font-weight: normal;
vertical-align: 1px;
text-shadow: 1px 1px rgba(255,255,255,0.6);
}
.pm-log {
    min-height: 100px;
    max-height: 300px;
    background: rgba(242,247,250,.925);
    color: black;
    word-wrap: break-word;
    border: 1px solid #AAAAAA;
    border-top: 0;
    border-bottom: 0;
    overflow: auto;
    -webkit-overflow-scrolling: touch;
    overflow-scrolling: touch;
}

.pm-log-add {
    border: 1px solid black;
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
background: rgba(0,129,255, 0.8);
  transition: background 0.4s;
}

.pm-log-add:hover {
background-color: rgba(255,30,30, 0.8);
border-color: black;
}

.pm-window.collapsed .pm-log,.pm-window.collapsed .pm-log-add {
    display: none;
}

.pm-window.focused h3,.pm-window.focused .pm-log,.pm-window.focused .pm-log-add {
    border-color: #555555;
}

.pm-window.focused h3 {
background: rgba(255, 255, 255, 0.5);
color: #0081FF;
text-shadow: 1px 1px black;
border: 1px solid #0081FF;
cursor: url(http://cur.cursors-4u.net/games/gam-13/gam1281.cur), progress !important;
  transition: background 0.4s, color 0.5s, text-shadow 0.5s;
}
 
.pm-window.focused h3:hover {
background: rgba(0, 0, 0, 0.5);
color: #0081FF;
text-shadow: 1px 1px 1px rgba(255,255,255,0.4);
border: 1px solid #0081FF;
cursor: url(http://cur.cursors-4u.net/games/gam-13/gam1281.cur), progress !important;
}

.challenge {
    margin-top: -1px;
    background: rgba(255,30,30,0.8);
    border: 1px solid rgba(0,129,255,0.9);
    color: white;
  text-shadow: 1px 1px rgba(0,0,0,0.4);
    padding: 1px 5px;
}

.challenge p {
    margin: 4px 0;
}

.tiny-layout .leftmenu,.tiny-layout .rightmenu,.tiny-layout .activitymenu {
    position: static;
    top: 0;
    right: 0;
    left: 0;
    width: auto;
    margin: 0;
    padding: 0;
}

.tiny-layout .leftmenu {
    padding-top: 30px;
}

.tiny-layout .activitymenu {
    padding-bottom: 8px;
}

.mainmessage {
    display: block;
    padding: 10px 15px;
    margin: 30px 40px;
    max-width: 400px;
    border-radius: 20px;
    background: rgba(201,207,219,.8);
    color: black;
}

.menugroup {
    display: block;
    padding: 10px 0;
    margin: 0 27px 30px 27px;
    max-width: 400px;
    border-radius: 20px;
    background: rgba(201,207,219,.6);
    color: black;
    text-shadow: 1px 1px 1px white;
}

.menugroup {
    background: rgba(201,207,219,.4);
    text-align: center;
}

.mainmessage p,.menugroup p {
    margin: 10px 0;
}

.menugroup .button {
    box-sizing: border-box;
    font-size: 12pt;
    font-family: Verdana,Helvetica,Arial,sans-serif;
    display: inline-block;
    padding: 5px 7px 3px 7px;
    width: 200px;
    outline: none;
    cursor: pointer;
    text-align: center;
    text-decoration: none;
    text-shadow: 0 -1px 0 hsl(210,40%,10%);
    border-radius: 5px;
    -webkit-box-shadow: 0 1px 2px rgba(0,0,0,.2),inset 0 1px 1px rgba(255,255,255,.8);
    -moz-box-shadow: 0 1px 2px rgba(0,0,0,.2),inset 0 1px 1px rgba(255,255,255,.8);
    box-shadow: 0 1px 2px rgba(0,0,0,.2),inset 0 1px 1px rgba(255,255,255,.8);
    color: #FFFFFF;
    border: solid 1px #AAAAAA;
    background: linear-gradient(rgba(0,129,255,0.9), rgba(0,60,230,0.9));
    border-color: rgba(0,0,0,0.9);
  transition: border 0s;
}

.menugroup .button:hover {
    background: linear-gradient(rgba(255,30,30,0.9), rgba(230,0,0,0.9));
    border-color: rgba(0,129,255,0.9);
}

.menugroup button.big {
    width: 230px;
    padding: 6px 7px 6px 7px;
    font-size: 14pt;
}

.menugroup .button:active {
    box-shadow: 0 1px 2px rgba(0,0,0,.2),inset 0 1px 1px rgba(255,255,255,.8);
    background: linear-gradient(rgba(210,20,20,0.9),rgba(200,0,0,0.9));
    border-color: rgba(0,129,255,0.9);
}

.menugroup .button.disabled,.menugroup .button.disabled:hover,.menugroup .button.disabled:active {
    cursor: url(http://cur.cursors-4u.net/games/gam-13/gam1281.cur), progress !important;
    background: #EEEEEE;
    border: 1px solid #CCCCCC;
    color: #999999;
    text-shadow: none;
    box-shadow: 0 1px 2px rgba(0,0,0,.1);
}

.rightmenu {
    width: 294px;
}

.rightmenu .menugroup .button {
    width: 180px;
}


.ladder table,.ladder td,.ladder th {
    border-collapse: collapse;
    border: 1px solid rgba(0,0,0,0.9);
}

.ladder td,.ladder th {
    padding: 3px 5px;
