css:
@-moz-document url-prefix(http://instasync.com/) {
.navbar-fixed-top, .navbar-fixed-bottom {
position: absolute
}
a.navbar-brand {
color: #000;
visibility:hidden;
}
a.navbar-brand:before {
content: "poipleSync";
visibility:visible;
color: #fff;
  }
body.dim .navbar-default {
background-image: linear-gradient(to bottom, #9664ff 0, #9664ff 100%);
border-bottom: 1px solid #9664ff;
}
a {
color: #326496;
text-decoration: none;
}
.navbar-default .navbar-nav>li>a {
color: #FFF;
}
.room-container .room-component-row {
box-shadow: 0 6px 12px rgba(0,0,0,0.276);
background-color: #9664ff;
background-image: none;
padding: 0px;
border: 0px;
margin: 0px;
}
.room-container .room-component-row .video-controls {
border-right: #9664ff;
}
body.dim .room-container .room-component-row .video-controls .tab-content {
background-color: #9664ff;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li.active {
background-color: #320064;
color: #FFFFFF;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li {
background-color: #dcdcff;
border-radius: 4px;
border-bottom: 1px solid #b8b8b8;
padding: 4px;
/* font-size: 12px; */
color: #634A74;
cursor: pointer;
}
.room-container .room-component-row .chat {
border-bottom: 1px solid #320064;
background-image: url("http://i.imgur.com/7jyU77N.jpg");
background-color: #ffffff;
}
.nav-tabs.chat-tabs {
background-image: linear-gradient(to right, #fff 0, #320064 55%);
}
body.dim .room-container .room-component-row .video-controls {
-webkit-transition: all 1.5s ease;
-moz-transition: all 1.5s ease;
-o-transition: all 1.5s ease;
transition: all 1.5s ease;
background-color: #dcdcff;
}
body.dim {
background-image: linear-gradient(to bottom,#320064 0,#000000 100%);
background-color: #494949;
}
.btn-primary {
background-image: linear-gradient(#320064, #fff 200%);
background-color: #ffffff;
border-color: #000;
}
.btn-primary:hover, btn-primary:focus { 
background-color: #00ff00;
background-image: none;
background-position: 0 -15px;
color: #000000;
}
#poll_tab .poll.active, #poll_column .poll.active {
box-shadow: 0 0 8px #808080;
background-image: linear-gradient(to bottom,#ffffff 0,#c8c8ff 100%);
}
.text-info {
color: #000000;
}
.room-container .room-info-row {
background-image: none;
}
.room-container .room-info-row .overview {
        border-bottom: #ff55ff solid
}}
