css:
@-moz-document url-prefix(http://instasync.com/) {
    body {
        background-color: black;
}
body.dim .navbar-default {
background-image: linear-gradient(to bottom, #9664ff 0, #9664ff 100%);
background-image: -webkit-gradient(linear,left top,left bottom,from(#4908e9),to(#d5627d));
}
a {
color: #070509;
text-decoration: none;
}
.navbar-default .navbar-nav>li>a {
color: #FFF;
}
.room-container .room-component-row {
background-image: -webkit-gradient(linear,left top,left bottom,from(#44d9e8),to(#9049fc));
padding: 0px;
margin: 0px;
}
body.dim .room-container .room-component-row .video-controls .tab-content {
background-image: -webkit-gradient(linear,left top,left bottom,from(#000000),to(#000000));
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li.active {
background-image: -webkit-gradient(linear,left top,left bottom,from(#44d9e8),to(#9049fc));
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li {
background-image: -webkit-gradient(linear,left top,left bottom,from(#53d2b7),to(#7d7255));
border-radius: 4px;
border-bottom: 1px solid #b8b8b8;
padding: 4px;
/* font-size: 12px; */
color: #634A74;
cursor: pointer;
}
.room-container .room-component-row .chat {
background-image: -webkit-gradient(linear,left top,left bottom,from(#abd3db),to(#eec788));
width: 100%;
background-color: #ffffff
}
body.dim .room-container .room-component-row .video-controls {
-webkit-transition: all 1.5s ease;
-moz-transition: all 1.5s ease;
-o-transition: all 1.5s ease;
transition: all 1.5s ease;
background-color: #dcdcff;
}
body.dim {
background-image: -webkit-linear-gradient(top,#ab63ea 0,#DAC1E7 100%);
background-image: -o-linear-gradient(top,#A88FB5 0,#320064 100%);
background-image: -webkit-gradient(linear,left top,left bottom,from(#A88FB5),to(#DAC1E7));
background-image: url("http://i.imgur.com/bxtUhsX.jpg");
}
.btn-primary {
background-image: -webkit-linear-gradient(top,#A88FB5 0,#DAC1E7 100%);
background-image: -o-linear-gradient(top,#A88FB5 0,#DAC1E7 100%);
background-image: -webkit-gradient(linear,left top,left bottom,from(#A88FB5),to(#DAC1E7));
background-image: linear-gradient(to bottom,#2681ec 0,#a4e57f 100%);
border-color: #A98BBD;
}
.btn-primary:hover, .btn-primary:focus { 
background-color: #c8c8ff;
background-image: linear-gradient(to bottom,#ffffff 0,#ffc8c8 100%);
background-position: 0 -15px;
color: #000000;
}
}
