css:
@-moz-document url-prefix(http://instasync.com/) {
    body {
        background-color: black;
  }}
/* i really want this to be global */
/* lolwut */
body hr{background: #666 none!important; color: #666!important; border:1px solid #666!important; height: 1px!important; overflow:hidden!important; display: block!important;}
* body a, body a *{color: #aea06b!important; 
}
body a:hover, body a:hover *{color: #766b41!important; text-decoration: underline!important;
}
body.dim .navbar-default {
background-image: linear-gradient(to bottom, #a7a7a7 0, #000000 100%);
border-bottom: 1px solid #272727;
font-size: 16.5px;
}
body table:not(:empty){background-color:#272727!important}
body table>tbody>tr:nth-child(even){background-color:#272727!important}
body table>tbody>tr>td:nth-child(odd){background-color:#272727!important}
body.dim .room-container .room-component-row .video-controls .tab-content, .nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus, .room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist, .room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li {background-color: #272727; color: #fff;
margin-top: 1px;
margin-bottom: 1px;
}
a {
color: #000;
text-decoration: none;
}
.navbar-default .navbar-nav>li>a {
color: #000;
}
.room-container .room-component-row {
box-shadow: 0 6px 12px #fff;
background-color: #272727;
padding: 0px;
margin: 0px;
width: 102.5%;
}
.body.dim .room-container .room-component-row .video-controls .tab-content {
background-color: #272727;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li.active {
background-color: #171717;
color: #fff;
font-size: 13px;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li {
background-color: #575757;
border-radius: 4px;
border-bottom: 1px solid #fff;
padding: 4px;
font-size: 12px;
color: #070707;
cursor: pointer;
}
.room-container .room-component-row .chat {
width: 100%;
background-color: #fff;
color: #000;
}
input {background-color: #000; color: #fff;}

body.dim .room-container .room-component-row .video-controls {
-webkit-transition: all 1.5s ease;
-moz-transition: all 1.5s ease;
-o-transition: all 1.5s ease;
transition: all 1.5s ease;
background-color: #c7c7c7;
color: #000000;
}
body.dim {
background-image: -o-linear-gradient(top,#A88FB5 0,#50c875 100%);
background-image: -webkit-gradient(linear,left top,left bottom,from(#A88FB5),to(#50c875));
background-image: linear-gradient(to bottom,#505050 0,#000000 100%);
background-color: #494949;
width: 97.5%;
}
.room-container .room-component-row .video-controls .nav-tabs {
  background-color: #131313;
  color: #ffffff;
}
.btn-primary {
background-image: -webkit-linear-gradient(top,#070707 0,#575757 100%);
background-image: -o-linear-gradient(top,#070707 0,#575757 100%);
background-image: -webkit-gradient(linear,left top,left bottom,from(#070707),to(#575757));
background-image: linear-gradient(to bottom,#070707 0,#575757 100%);
border-color: #fff;
}
.btn-primary:hover, .btn-primary:focus {
background-image: linear-gradient(to bottom,#000000 0,#575757 200%);
background-color: #575757;
background-position: 0 -2.5px;
border-color: #ffffff;
color: #fff;
}
#poll_tab .poll.active, #poll_column .poll.active {
box-shadow: 0 0 8px rgba(0,0,0,0.25);
background-color: white;
background-image: linear-gradient(to bottom,#c7c7c7 0,#272727 200%);
color: #171717;
font-size: 13px;
}
.text-info {
color: #333;
}
::-webkit-scrollbar {
  width: 11px;
  height: 11px;
}
::-webkit-scrollbar-button {
  width: 2px;
  height: 2px;
}
::-webkit-scrollbar-thumb {
  background: #696969;
  border: 0px none #393939;
  border-radius: 15px;
}
::-webkit-scrollbar-thumb:hover {
  background: #535353;
}
::-webkit-scrollbar-thumb:active {
  background: #484848;
}
::-webkit-scrollbar-track {
  background: #1b1b1b;
  border: 0px none #ffffff;
  border-radius: 0px;
}
::-webkit-scrollbar-track:hover {
  background: #1b1b1b;
}
::-webkit-scrollbar-track:active {
  background: #1b1b1b;
}
::-webkit-scrollbar-corner {
  background: transparent;
}
