css:
@-moz-document domain("wowhead.com") {
    /* Hide Adverts */
.horizontal-block-bg
.is-atf,
#ad-horizontal,
#ad-skin,
#ac-lre-player,
#video-pos-static,
.zamaf-bt,
.blocks,
.sidebar-wrapper,
#ad_unit,
#zamaf-dynamic-unit-unit-au-0,
.zamaf-dynamic-unit
.zamaf-au
.dynamic-ad
{
    display: none !important;
    width: 0;
    height: 0;
}

/* Hide WoWHead Premium */
.premium-promo-row {
    display: none !important;
    width: 0;
    height: 0;
}

/* Hide Share Buttons */
.share-links {
    display: none !important;
    width: 0;
    height: 0;
}

/* Fix sidebar */
.page-content {
    padding-right: 0;
}
    /* Remove Footer? */
    /*[[footer]]*/
}
