@namespace url(http://www.w3.org/1999/xhtml);

/* RYM Black & Blue by THPZ */
/* LAST UPDATED: Jan. 30, 2016 */
@-moz-document domain("rateyourmusic.com") {
  /* NAV BAR */
  #navtop {
    border-bottom: 1px solid #223;
  }
  #navtop li {
