css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("infocpp.iit.bme.hu") {
body{
  
  background-color: black;
  font-family: Verdana;
  background-image: url("http://shinnok.com/rants/wp-content/uploads/2011/06/minimalist-wallpaper-black.jpg");
  background-position:center; 
  background-repeat:no-repeat;
  background-attachment: fixed;
  background-size: cover;
}
 #header {
    background-image: url("http://verychard.com/university/infocpp_head.png");
    background-repeat: no-repeat;
    min-height: 60px;
}

  .node table.fancy th {
    background-color: #cfcfcf;
}
  
  
.node table.fancy td{
    background-color: #e6e6e6;
}
  
  
.node table.fancy tr:nth-child(2n+1) td {
    background-color: #efefef;
}
  .node table.fancy tr td.green, .green {
    background-color: #c7eabc;
}
  
  
#navigation {
    background-color: #bc0908;
    color: white;
    font-weight: bold;
    text-align: center;
    text-decoration:none;
    width: 960px;
}
 
 #content, .no-sidebars #content {

    margin-left: 0;
    padding: 0;
    width: 960px;
}
  
  
 #navigation ul.links, #navigation .content ul{
   width: 960px;
   margin: 0 auto 0 80px;
   padding: 2px;
 }
  
  
  #main-menu li a:hover{
    background-color: #73605D;
  }
  
  #page a:hover{
    color: #f77105;
  }
  
  #main-menu li a.active {
  background-color: white;
    color: pink;
  }
 
  #main-menu li a{
    padding: 1px 13px 1px 13px;
    display: inline;
    float:none;
    background-color: black;
    color:white;
    text-transform: uppercase;
    font-weight: 600;
    font-size: 12px;
    text-decoration: none;
  }
  #main-menu li a:link{
    color:white;
  }
  
  #main-menu li a.active a{
    color:#D90A2E;
  }
  
  #edit-submit{
    background-color: orange;
    border: 2px solid black;
    color: white;
    height: 25px;
    cursor: pointer;
  }
  
  #edit-submit:hover{
    color: orange;
    background-color: white;
  }
  
  #navigation ul.links li a, #navigation .content li a{
    
    color:white !important;

  }
  
  #navigation ul.links li a.active, #navigation .content li a.active{
    
    color:#D90A2E !important;
  }
  
  #navigation ul.links li, #navigation .content li{
   margin: 0 auto 0 5%;
    
  }
  #main-menu li a.active {
    
    position: inherit;
  }
  
  div.sidenote, .note{
     color: white!important;
   padding: 10px!important;
    border: 3px solid black!important;
    background-color:rgba(00, 00, 00, 0.7)!important;
  }
  
 kbd{
    color: red;
    
  }
  
  .slide{
    width: 700px;
    margin: 0 auto;
  }
  
  .slides{
    margin-bottom: 50px;
  }
  
#user-box a {border: 1px solid black;
    color: white;
    padding: 2px 15px;
    text-decoration: none;
    background-color: #e54d02;
}
  
 #user-box a:hover{
   background-color: white;
   color:#e54d02;
 } 
  
#help-box a{
  border: 1px solid black; 
  background-color: white;
  font-weight:bold; 
  margin-right: 10px;
  text-decoration: none;
  padding: 2px 10px 2px 10px;
  color:red;
  font-family:Verdana;
}
  
  #help-box a:hover{
    background-color: red;
    color:white;
  }
 
  #content{
    background-color: #1d1e20;
    color: white;
  }
  
  #content a{
    color: red;
    font-style: italic;
    text-decoration: none;
  }
  
  #local-login #login-form .submit > input{
    margin: 0;
    width: 11em;
  }
  
  .submit > input {
  width: 40%;
  margin: 0 0 0 30%;
    background-color: orange;
    border: 2px solid black;
    color: white;
    
    text-transform:uppercase;
    font-weight: bold;
}
.submit > input:hover{
  background-color: green;
}
  .submit-files h4 {
    background-color: transparent;
    border: 0px transparent none;
  }
  
  .submit-files h4:hover{
    color:orange;
  }
  
  h4, .CodeMirror{border: 1px solid black;}


.js .exercise-info #detail:hover{
  color:orange;
  cursor: pointer;
}

.region-footer {
    display:none;}
  
 #page{
   width: 960px;
   background-color: #bc0908;
   padding-top: 20px;
   margin: 10px auto 10px;
   border: 5px solid black;
 } 
}
