@-moz-document url-prefix("https://recure.facebook.com"), domain("apps.facebook.com"), regexp("https?://www\\.facebook\\.com/(?!plugins/).*") {
.titlebar, .fbNubFlyoutInner, .fbNubFlyoutOuter, .fbDockChatTabFlyout, .fbNubButton, .fbNub, .fbNubFlyoutTitlebar {
	border-top-left-radius: 10px !important;
    border-top-right-radius: 10px !important;
}
.titlebar, .fbNubButton, .fbNubFlyoutTitlebar {
	 -moz-box-shadow:inset 0px 1px 0px 0px #a8a8a8 !important;
     -webkit-box-shadow:inset 0px 1px 0px 0px #a8a8a8 !important;
     box-shadow:inset 0px 1px 0px 0px #a8a8a8!important;
     background:-webkit-gradient(linear, left top, left bottom, color-stop(0.05, #7d7d7d), color-stop(1, #000000));
     background:-moz-linear-gradient(top, #7d7d7d 5%, #000000 100%);
     background:-webkit-linear-gradient(top, #7d7d7d 5%, #000000 100%);
     background:-o-linear-gradient(top, #7d7d7d 5%, #000000 100%);
     background:-ms-linear-gradient(top, #7d7d7d 5%, #000000 100%);
     background:linear-gradient(to bottom, #7d7d7d 5%, #000000 100%);
     color: #fff;
}
.highlightTitle .fbNubButton, .highlightTitle .titlebar {
	background:-webkit-gradient(linear, left top, left bottom, color-stop(0.05, #000000), color-stop(1, #7d7d7d)) !important;
    background:-moz-linear-gradient(top, #000000 5%, #7d7d7d 100%) !important;
    background:-webkit-linear-gradient(top, #000000 5%, #7d7d7d 100%) !important;
    background:-o-linear-gradient(top, #000000 5%, #7d7d7d 100%) !important;
    background:-ms-linear-gradient(top, #000000 5%, #7d7d7d 100%) !important;
    background:linear-gradient(to bottom, #000000 5%, #7d7d7d 100%) !important;
}
.fbDockChatTabFlyout .fbNubFlyoutBody {
	background: url("https://pbs.twimg.com/media/B8JLEruCAAAmsNb.jpg:large") top left no-repeat #000000!important;
    background-size: 240px auto !important;
    border-color: #000 !important;
}
.fbNubFlyoutFooter {
	border-color: #000 !important;
}
._510g.sentFromMobile ._510h {
	background-position: -300px -554px !important;
}
._510g.seen ._510h {
	background-position: -78px -260px !important;
}
.fbChatConvItem .livetimestamp, .label .count * {
  	color: #fff !important;
}
.fbChatConvItem .messages {
    color: #7d7d7d !important;
}
.fbChatConvItem {
	margin: 0 !important;
    padding: 5px !important;
}
.fbDockChatTabFlyout .pts:hover {
	background-color: #fff !important;
    opacity: 0.9;
}
.fbChatConvItem:hover > .messages{
	color: #000 !important;
}
.fbDock {
	margin: 0 4px 0 0;
}
.fbDockChatTabFlyout, .fbNub {
	-webkit-transition: 1s opacity;
    opacity: 1;
}
.fbDockChatTabFlyout:hover, .fbNub:hover {
	opacity: 1;
}
.fbChatTab .funhouse .close:active, .fbChatTab .funhouse .close:hover, .fbChatTab .funhouse .close:focus {
background-position: -8px -393px;
}
.titlebarButtonWrapper ._510p .button:hover, .titlebarButtonWrapper ._510p .button:active, .titlebarButtonWrapper ._510p .button:focus {
	background-color: transparent !important;
}
}
