css:
@-moz-document domain(ordway.umt.edu) {

body{
background-color: #000; 
}

#wrap{ }


a{
color: #4A5283;
}

a:hover{
color: #4EB0B0;
}

#sidebar h1 {
color: #2B5964;
}

.sidebarnav a {
background-color: rgba(0, 5, 54, 0);
border: 1px solid rgba(176, 176, 176, 0.14);
color: #ADADAD;
display: block;
margin-top: 8px;
padding: 2px 4px 2px 4px;
text-transform: uppercase;
width: 140px;
height: 15px;
}

.sidebarnav ul li a {
text-indent: 16px;

background: rgba(0, 5, 54, 0);
border: 1px solid rgba(176, 176, 176, 0.14);
text-transform: none;
margin-top: 0;
border-top: 0;
}

.sidebarnav ul li a:hover{border-color: rgba(176, 176, 176, 0.14);}



#container {
background: url(http://content.wallpapers-room.com/resolutions/1920x1200/s/Wallpapers-room_com___simple_wall_glass_by_lassekongo83_1920x1200.jpg) repeat-x;
}


#content {
border: 0;
}

#sitename {
display: none;
}

#mainmenu {
margin: 20px 0 25px 189px;

}


#sidebar{ margin-top: -58px; }

#sidebar .searchform{
margin: 0 0 38px 0;
}

#sidebar input {
background-color: rgba(6, 8, 48, 0.37);
border: 1px solid rgba(255, 255, 255, 0.28);
height: 21px;
text-indent: 6px;
font-size: 11px;
}

#widget_officeSchedule, 
#widget_deptSchedule {display: none;}


.due_alert TD {
background-color: #4A5283;
}

span.ticketStatusreopened {
background-color: #97C592 !important;
border: 1px solid #296B22 !important;
color: #516252 !important;
}

span.ticketStatusonhold {
background-color: #676B8B;
border: 1px solid #141841;
color: #D9D8E5;
}

.ticketStatusopenPullout, .ticketStatusreopenedPullout {
border-color: #296B22 !important;
}

.SACommentAddForm .SACommentFollowers span.followers a{
background-color: #97C592 !important;
border: 1px solid #296B22 !important;
color: #0A4D0E !important;
}

}
