css:
@-moz-document url-prefix(http://instasync.com/) {
    body {
        background-color: black;
}
body.dim .navbar-default {
background-image: linear-gradient(to bottom, #FF69B4 0, #1874CD 100%);
border-bottom: 1px solid #505050;
}
a {
color: #326496;
text-decoration: none;
}
    a.navbar-brand:before {
content: "HotSync";
visibility:visible;
color: #fff;
  }
.navbar
.navbar-default .navbar-nav>li>a {
color: #fff;
}
.room-container .room-component-row {
box-shadow: 0 6px 12px rgba(0,0,0,0.276);
background-image: linear-gradient(to top, #FF69B4 0%, #1874CD 30%);
padding: 0px;
margin: 0px;
}
    .column{
    background-image: linear-gradient(to bottom,#FF69B4 0%,#1874CD 15%);
}
body.dim .room-container .room-component-row .video-controls .tab-content {
background-image: linear-gradient(to bottom,#1874CD 0,#1874CD 100%);
    color: #ffffff;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li.active {
background-image: linear-gradient(to bottom,#1874CD 0,#1874CD 100%);
color: #FF69B4;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li {
background-color: #FF69B4;
border-radius: 4px;
border-bottom: 1px solid #b8b8b8;
padding: 4px;
/* font-size: 12px; */ 
color: #000000;
cursor: pointer;
}
    .room-container .room-component-row .video-controls .nav-tabs {
        background-image: linear-gradient(to bottom,#FF69B4 0%,#1874CD 100%);
}
.room-container .room-component-row .chat {
width: 100%;
background-color: #ffffff;
color: #000000
}
body.dim .room-container .room-component-row .video-controls {
-webkit-transition: all 1.5s ease;
-moz-transition: all 1.5s ease;
-o-transition: all 1.5s ease;
transition: all 1.5s ease;
background-color: #c7c7c7;
color: #000000
}
    .nav-tabs.chat-tabs {
background-image: linear-gradient(to right, #fff 70%, #FF69B4 80%, #1874CD 100%);
    }
body.dim {
background-image: -o-linear-gradient(top,#A88FB5 0,#50c875 100%);
background-image: -webkit-gradient(linear,left top,left bottom,from(#A88FB5),to(#50c875));
background-image: linear-gradient(to right, #FF69B4 0, #1874CD 10%, #1874CD 90%, #FF69B4 100%);
background-color: #494949;
}
.btn-primary {
background-image: -webkit-linear-gradient(top,#000000 0,#000000 100%);
background-image: -o-linear-gradient(top,#000000 0,#000000 100%);
background-image: -webkit-gradient(linear,left top,left bottom,from(#000000),to(#000000));
background-image: linear-gradient(to bottom,#000000 0,#505050 100%);
border-color: #808080
}
.btn-primary:hover, .btn-primary:focus {
background-image: linear-gradient(to bottom,#101010 0,#c8c8c8 100%);
background-color: #c8c8c8;
background-position: 0 -15px;
border-color: #ffffff;
color: #000000;
}
#poll_tab .poll.active, #poll_column .poll.active {
box-shadow: 0 0 8px rgba(0,0,0,0.25);
background-color: white;
background-image: linear-gradient(to bottom,#ffffff 0,#c7c7c7 100%);
}
.text-info {
color: #000000
}
}
