.navigationTopicBreadcrumbs li {
  font-family: "Verdana", sans-serif !important;
  font-size: 130% !important;
}
  
/*most links in forum pages*/
  
.hbs a.colorLink:link {text-decoration: underline; color:#00008B !important;}
.hbs a.colorLink:visited {color:#380038 !important;}  
.hbs a.colorLink:hover {color:#5D198B !important;}

/*list of links to all forums*/

a.topic-link:link {color:#00008B !important;}
a.topic-link:visited {color:#380038 !important;}
a.topic-link:hover {color:#5D198B !important;}


.hbs .small-text {
    font-size: 108% !important ;
