css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("instela.com") {
  body {background-color: #2C2828;}
  h1.title a{
    color:#11A6B5;}
  nav#bgleft > *, .right > ul > li, .entry_input, .inner_page, .white_section {
    background-color:#4D4D4D;
    border:none!important;
  }
  ul.floating > li.tag a.link-button:hover {
    background-color:#989898;
  }
  .alternative-header-lamps li > a:hover,ul.top_buttons > li > a:hover {
    color:#d2d2d2!important;
  }
  h1, h2 {
    color:#11A692 !important;
  }
  #solframe li span.title-section {
    color:#d2d2d2;
  }
  /* yazi secim */
  ::-moz-selection {
    background: #fff;
    color:#000;
    
  }
  #hovercard .hovercard_inner .nick h2 {
    color:#1ABFB9 !important;
  }
  .entry-tags > div a{
    color:#fff;
    background:none repeat scroll 0% 0% #777;
  }
  #SLto, #SLfrom {
    color:#000!important;
  }
  .send-message > button{
    color:#fff;
  }
  .send-message > button:hover{
    color:#d2d2d2;
  }
  .entry-comments form .comment-table > button:hover{
    color:#d2d2d2;
  }
  .search-results .search-result {
    color:#fff;
    width:100%;
  }
  .alternative-header-lamps li.sahsim .floating a,.alternative-header-lamps li > a{
    color:#fff!important;
  }
  ::selection {
      background: #fff;
      color:#000;
  }
  /* sol frame taban cizgisi */
  #bgleft .lst li {
    border-bottom:1px solid #2a2a2a;
  }
  /* baslik  */
  header.main_header #top_buttons .floating_opener > a {
    border:none!important;
  }
  
  /* yeni baslik var baloncugu*/
  .sf_section > span.news {
    background:#999;
    color:#000!important;
  }
  nav#bgleft > :hover {
    background:#4D4D4D;
  }
  
  .index-header,.view-all-page{
    border:none!important;
  }
  /* kategori listesi acilir menu */
  .index-header .selector .floating {
    background-color:#6c6c6c;
  
  }
  body.no-touch .featured .sf_desc a:after, body.no-touch .featured a:after {
  background:#999!important;
  }
  /* sol frame tekil baslik hover stil */
  body.no-touch #bgleft #solframe li:hover, body.no-touch #bgleft #solframe li.selected, body.no-touch #bgleft #solframe a.x {
    background:#999!important;
  }
  /* sol frame x butonundan golgeyi kaldir */
  #bgleft #solframe li > a.x {
    text-shadow:none!important;
  }
  /* sol frame sol cizgi */
  body.no-touch #solframe li:hover {
    border-left:4px solid #757575;
  }
  /* sol frame yenile yazi */
  .sf_section {
    color:#E3E3E3!important;
  }
  /* sol frame yazi rengi */
  #bgleft a {
    color:#C6C6C6;
  }
  /* ileri geri */
  .pager_short a {
    color:#999!important;
  }
  /*giriler*/
  .entries > article{
    background: #4D4D4D !important;
    color:#eee; 
    border:none!important;
  }
  /*devamini gor*/
  .entries > article .entry-resume{
    background:none repeat scroll 0% 0% #484848;
  }
  /*profil kapak*/
  .profile > .profile_header{
    background:#575757;
    border:none!important;
  }
  /* yazar adi */
  .entries .entry_header .dateauthor > a.hop {
    color:#11A6B5;
  }
  /* top baslik */
  header.main_header {
    background: #474747!important;
    border-bottom:none!important;
  }
  /* baslik ii navigasyon */
  .index-header .selector li > a{
    color:#fff;
  }
  /* baslik ici navigasyon arkaplan */
  .index-header {
    background-color: #4d4d4d;
  }
  /* panel acilir menu */
  .panelmenu .content > ul > li > a:hover {
    background-color: #4d4d4d;
  }
  .panelmenu .content > ul > li > a {
    color:#fff;
  }
  /* giri ekle */
  .entry-comments {
    background-color:#4D4D4D;
  }
  .entry-comments > *{
    border-top:1px solid #787878;
  }
  article.entry-object.edit-enable .edit-entry textarea {
    background-color:#686868;
  }
  .form-group input{
    background:#4d4d4d!important;
  }
  /* gonder giri form */
  .entry-comments form .comment-table > button {
    color:#fff;
  }
  /* giri ust navigasyon */
  .title-actions a {
    color:#fff;
  }
  #hovercard .hovercard_inner {
    background:none repeat scroll 0% 0% #696969;
  }
  /*giri secenekler */
  div.entry_actions i.fa {
    color:#c3c3c3;
  }
  /* giris ecenekler icerik */
  div.entry_actions > .floating {
    background-color:#777;
  }
  /* giri secenekler icerik yazi rengi */
  div.entry_actions > .floating ul > li > a {
    color:#fff;
  }
  div.entry_actions > .floating ul > li > a:hover {
    background: none repeat scroll 0% 0% #5C5C5C;
  }
  /* buton hover arkaplan */
  input[type="button"]:hover, input[type="submit"]:hover, .but:hover {
    background:#696969!important;
  }
  .index-header .selector li > a:after  {
    background:none repeat scroll 0% 0% #91A5A7;
  }
  .selector li:hover a {
    color:#fff!important;
  }
  /* giri alt navigasyon */
  .entries footer {
    border-top:1px solid #7d7d7d;
  }
  .entries footer > div > div button:hover, .entries footer > div > div a:hover {
    color:#d5d5d5;
  }
  /* sol frame hover yazi rengi tekil */
  #solframe li:hover .title-section{
    color:#000;
  }
  .entry_text a{
    color:#26C3F6!important;
  }
  /* duyurular*/
  .right-section > .right-box,.right-section > .right-box:hover {
    background-color:#4d4d4d;
    border:none!important;
  }
  /*duyurular baslik vs*/
  .right-box.duyurular .duyuru a, .right-box.duyurular .related a, .right-box.duyurular .top20 a, .right-box.related .duyuru a, .right-box.related .related a, .right-box.related .top20 a {
    color:#d2d2d2;
    border-color:#7d7d7d;
  }
  /* duyurular hover */
  .right-box.duyurular .duyuru a:hover, .right-box.duyurular .related a:hover, .right-box.duyurular .top20 a:hover, .right-box.related .duyuru a:hover, .right-box.related .related a:hover, .right-box.related .top20 a:hover {
    background-color: #6f6f6f;
  }
  .right-box.radio .radio-content .program-info .program-name {
    color:#fff;
  }
  .now-playing{
    color:#fff;
  }
  .now-playing i.fa{
    color:#fff!important;
  }
  /* giris formu */
  .stargate > form {
    background-color:#454545;
    color:#eee;
  }
  /* link hover */
  body.no-touch a:hover {
    color:#eee;
  }
  /* form buton hover */
  input[type="button"]:hover, input[type="submit"]:hover, .but:hover {
    background-color:#a5a5a5;
  }
  /* form buton */
  input[type="button"], input[type="submit"], .but{
    background-color: #787878;
  }
  /* ziyaretci sol frame  */ 
  body.not-logged #solframe li {
    background-color: none repeat scroll 0% 0% #3F3F3F;
    border:none!important;
  }
  /* ziyaretci frame baslik aciklama */
  body.not-logged #solframe li span.teaser {
    color: #CECECE;
  }
  .avatar-container > input#avatar_upload{
    display:none;
  }
  .dateauthor > a.hop b:hover {
    color:#e1e1e1;
  }
  /* sabit baslik  */
  .fixed-title-header {
    background:none repeat scroll 0% 0% #474747;
    border-bottom:1px solid #eee;
  }
  header.main_header .floating_opener.open > a, header.main_header .floating_opener.floating_opener_hover:hover > a {
    color:#fff!important;
    background-color:#2C2828 !important;
    
  }
  .right-section.media > .media-header .snippet {
    color:#d2d2d2;
  }
  .right-section.media > .media-header .date-permalink .media-nick {
    color:#3BBCE1;
  }
  a.sendbutton {
    color:#fff!important;
  }
  #bgleft {
    background:none repeat scroll 0% 0% #2C2C2C;
  }
  /* yine sol frame */
  #bgleft #solframe li {
    background-color:#4d4d4d;
  }
  /* sol farme yeni giri sayisi */
  #bgleft .w.v.p > span:last-child{
    color:#eee;
  }
  /* mesaj paneli ic ve liste arkaplan */
  .panel.messages #messagesContainer, .message-popup.messages #messagesContainer {
    color:#eee;
    background-color:#474747!important;
    border:none!important;
  }
  .panel.messages #messagesContainer > .conversations, .message-popup.messages #messagesContainer > .conversations {
    background-color:#474747;
  }
  /* mesajlar zaman ayrac */
  .date-seperator span {
    color:#000;
  }
  /* mesaj zamani */
  .message-group time {
    color:#eee;
  }
  /* mesaj baslik */
  .panel.messages #messagesContainer > .conversations .conversation .message_header, .message-popup.messages #messagesContainer > .conversations .conversation .message_header {
    background-color:#474747;
  }
  /* mesaj yaz kutu cerceve */
  .panel.messages #messagesContainer > .conversations .conversation form, .message-popup.messages #messagesContainer > .conversations .conversation form {
    background-color:#474747
  }
  /* mesaj kutus yazzi kutusu */
  .panel.messages #messagesContainer > .conversations .conversation form .message_form, .message-popup.messages #messagesContainer > .conversations .conversation form .message_form {
    background:none repeat scroll 0% 0% #5D5D5D;
    margin:0px;
  }
  /* mesaj gonder butonu kenar cizgisi border */
  .panel.messages #messagesContainer > .conversations .conversation form .message_form .message_buttons > a, .message-popup.messages #messagesContainer > .conversations .conversation form .message_form .message_buttons > a {
    border-right:none!important;
  }
  #search_input {
    color:#292929 !important;
  }
  /* mesaj gonder butonu hover */
  .panel.messages #messagesContainer > .conversations .conversation form .message_form .message_buttons > a:hover, .message-popup.messages #messagesContainer > .conversations .conversation form .message_form .message_buttons > a:hover{
    background:none repeat scroll 0% 0% #7A7A7A;
  }
  /* mesaj listesi tekil hover */
  .panel.messages #messagesContainer > .threads #thread_list > a:hover, .panel.messages #messagesContainer > .threads #thread_list > a.selected, .panel.messages #messagesContainer > .threads #thread_list > a.unread, .message-popup.messages #messagesContainer > .threads #thread_list > a:hover, .message-popup.messages #messagesContainer > .threads #thread_list > a.selected, .message-popup.messages #messagesContainer > .threads #thread_list > a.unread {
    background-color:#696969;
  }
  .panel.messages #messagesContainer > .threads #thread_list > a > .thread_container, .message-popup.messages #messagesContainer > .threads #thread_list > a > .thread_container {
    border-top:1px solid #7D7D7D!important;
  }

  /*mesaj okundu ve diger beyaz yazi gereken yerler*/
  .panel.messages #messagesContainer > .threads #thread_list > a > .thread_container .read-info, .message-popup.messages #messagesContainer > .threads #thread_list > a > .thread_container .read-info, .panel.messages #messagesContainer > .threads #thread_list > a > .thread_container .nick, .message-popup.messages #messagesContainer > .threads #thread_list > a > .thread_container .nick,.panel.messages #messagesContainer > .threads #thread_list > a > .thread_container .date-info, .message-popup.messages #messagesContainer > .threads #thread_list > a > .thread_container .date-info, .panel.messages #messagesContainer > .threads #thread_list > a > .thread_container .message-content, .message-popup.messages #messagesContainer > .threads #thread_list > a > .thread_container .message-content,.panel.messages #messagesContainer > .threads > form > div > div i.fa, .message-popup.messages #messagesContainer > .threads > form > div > div i.fa,.panel.messages #messagesContainer > .threads #thread_list > a.load_more, .message-popup.messages #messagesContainer > .threads #thread_list > a.load_more,#notification-popup li, .panel.notifications li,.entries > article.with-head-message .head-message,.entries > article.with-head-message .head-message > time,.entries footer > div > div button:hover, .entries footer > div > div a:hover,#hovercard .hovercard_inner .nick span,.panel section,.follow-block-title > .title a,.panel .disclaimer,.search-bar-widget i.fa,.entries footer > div > div button.darker > i, .entries footer > div > div a.darker > i,.lamb-text,span.lamb-text, li.lamps i.fa,.clairvoyance {
    color:#fff!important;
  }
  .clairvoyance {
    background:#666;
  }
  .related-titles-right .related-link {
    background-color:#4d4d4d;
  }
  .related-titles-right .related-link:hover {
    background-color:#7a7a7a;
  }
  button, input, optgroup, select, textarea {
    background:#4b4b4b;
  }
  .view-all-page:hover {
    background:none repeat scroll 0% 0% #202020;
  }
  .entry-comments form .comment-table > div textarea {
    border:1px solid #7A7A7A;
  }
  .follow-block-title > .title a:hover {
    color:#ECECEC;
  }
  footer.dip {
    background:none repeat scroll 0% 0% #333;
  }
  .gosterpager select {
    background-color:#424242;
  }
  .gosterpager a {
    color:#fff;
    background-color:#424242;
  }
  body.no-touch .gosterpager a:hover{
    background-color:#5c5c5c;
  }
  .new-entry-input {
    background-color:#5d5d5d;
  }
  .entry-comments .entry_text {
    color:#ECECEC;
  }
  .entry_text {
    color:#d2d2d2;
  }
  /* bildirimler arkaplan */
  .floating,.floating_opener.open .floating > .loadanim {
    background-color:#393939;
    border:none!important;
  }
  /* bildirimler tekil hover */
  #notification-popup li:hover, .panel.notifications li:hover {
    background-color:#545454;
  }
  /* bildirimler hepsini gor  */
  .top-popup #display_all{
    background-color:#262626;
    color:#fff;
    box-shadow:0px -6px 10px rgba(100, 100, 100, 0.65);
  }
  /* bildirimler hepsini gor hover */
  .top-popup #display_all:hover {
    background-color:#424242;
  }
  
  /* mesajlar konusmalarin devami butonu */
  .panel.messages #messagesContainer > .threads #thread_list > a.load_more, .message-popup.messages #messagesContainer > .threads #thread_list > a.load_more {
    background-color: #727272;
  }
  /* mesaj arama */
  .panel.messages #messagesContainer > .threads > form > div > div, .message-popup.messages #messagesContainer > .threads > form > div > div{
    background-color:#5d5d5d;
    border-color:#7A7A7A;
  }
  img.avatar {
    background-color:#fff;
  }
  .profile > .profile_header .avatar {
    background:none repeat scroll 0% 0% #636363;
  }
  /* profil alt taban */
  .profile > .profile_header > .profile_stats {
    background-color:#F6F6F6;
  }
  /* statu yazisi rengi */
  .status {
    color:#fff;
  }
  /* mesajlasma avatar */
  .message-group .profile-url > img,.panel.messages #messagesContainer > .threads #thread_list > a > .thread_container .avatar, .message-popup.messages #messagesContainer > .threads #thread_list > a > .thread_container .avatar {
    background-color:#fff; /* eger png ise arkaplan seffaf yerine beyaz olsun */
    border:2px solid #fff;
    border-radius: 10px;
  }
  .tag-recommendation{
    border-color:#6B6B6B;
  }
  .profile-name a{
    color:#11a6b5;
  }
  a{
    color:#11A6B5;
    text-decoration: none!important;
  }
  a.external{
    color:#11A6B5;
  }
  /* baslik navigasyon hover */
  .title-actions a:hover {
    color:#d2d2d2!important;
  }
  
  /* basliktaki diger girileri gor */
  .view-all-page,.panel section,.panel.friend-finder > section{
    background:#4d4d4d;
  }
  /* yazi kutulari */
  input, select, textarea {
    background-color: #5d5d5d;
    color:#FFF !important;
  }
  /* yeni giri cerceve */
  .new-entry-input {
    background-color:#626262;
  }
  
}
