css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(shero.dk)
{
	body
	{
		background-color: #000;
		color: #fff;
	}
	#root
	{
		background-color: #000; background:url('data:image/png;base64,') repeat;
	}
	.qc-cmp-ui
	{
		background-color: #1b1b1b;
	}
	.qc-cmp-title, .qc-cmp-button.qc-cmp-disable-button
	{
		color: #fff;
	}
	.qc-cmp-ui p, .qc-cmp-beta-messaging, .qc-cmp-main-messaging, .qc-cmp-messaging, .qc-cmp-bottom .qc-cmp-alt-action, #mobile-nav ul li a
	{
		color: #d1d1d1;
	}
	#qc-cmp-purpose-button::before
	{
		filter: invert(100%);
	}
	.qc-cmp-sub-title, .qc-cmp-ui .qc-cmp-show-vendors-link
	{
		color: #ac9752;
	}
	#top::before
	{
		background: #111;
		background-image: url('https://shero.dk/wp-content/themes/shero-2018/images/bg-a.png');
		background-image: url('https://shero.dk/wp-content/themes/shero-2018/images/bg-a.png'), linear-gradient(to bottom, #000000 10%,#303030 63%,#929292 100%);
		background-position: center center;
		background-repeat: no-repeat;
	}
	#nav > ul > li.active > a, #nav > ul > li > a:hover, #top .menu-icon:hover
	{
		color: #fff;
	}
	#featured h1 a, #featured h2 a, #featured h3 a, #featured h4 a, #featured h5 a, #featured h6 a, .news-a h1 a, .news-a h2 a, .news-a h3 a, .news-a h4 a, .news-a h5 a, .news-a h6 a, .list-b > li > a
	{
		color: #fff;
	}
	.news-a h1 span.hr span::before, .news-a h2 span.hr span::before, .news-a h3 span.hr span::before, .news-a h4 span.hr span::before, .news-a h5 span.hr span::before, .news-a h6 span.hr span::before, .news-a h1 span.hr span::after, .news-a h2 span.hr span::after, .news-a h3 span.hr span::after, .news-a h4 span.hr span::after, .news-a h5 span.hr span::after, .news-a h6 span.hr span::after, .list-b span.hr span::before, .list-b span.hr span::after, .scheme-b span::before, .scheme-b span::after, #root #featured span.hr span::before, #root #featured span.hr span::after
	{
		background: #929292;
	}
	#root #featured span.hr span, .news-a p
	{
		color: #bdbdbd;
	}
	.icon-facebook::before
	{
		filter: invert(100%);
	}
	.social-b li a i
	{
		color: #808080;
	}
	.news-a footer::before, .list-b footer::before
	{
		background: #222222;
	}
	.social-a li.text-right a
	{
		color: #aeaeae;
	}
	.link-a a
	{
		background: #222222;
		color: #fff;
	}
	.author-a figure
	{
		transition: all .3s ease-in-out;
	}
	.author-a figure:hover
	{
		transform: scale(1.5);
	}
	#mobile-nav 
	{
		background: linear-gradient(to bottom, #000000 10%,#303030 63%,#929292 100%);
	}

	/* If you're using a low resolution and the search + facebook button, goes below the navigations bar. Uncomment the code below to remove the facebook button and move the search button, into the navigation bar again. */

	/*#nav > ul:nth-child(2) {
		position: absolute;
		display: inline-flex;
	}
	#nav > ul:nth-child(2) > li:nth-child(1) {
		display: none;
	}*/
}
