/*
/*  User Styles Clean Dark v1.1
/*  Author: Kevin MacMartin - prurigro (https://userstyles.org/users/262769)
*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("userstyles.org") {
    body {
        background-color: #222;
        color: #ededed;
        font-size: 12px;
    }
    h1,
    h1 a {
        font-size: 18px !important;
    }
    h2 {
        font-size: 16px;
        font-style: normal !important;
    }
    a {
        color: #87d7ff;
    }
    a:hover {
        color: #f3f3f3 !important;
    }
    a:visited {
        color: #d75f5f;
    }
    header a {
        font-size: 14px;
    }
    #Head {
        background-color: #111;
    }
    #left-sidebar > *,
    #main-header {
        background-color: #171717;
    }
    #left-sidebar {
        margin-right: 6px;
    }
    #left-sidebar nav {
        min-width: 167px;
    }
    #search-terms {
        width: 122px !important;
    }
    input[type="search"] {
        padding-top: 1px !important;
    }
    input[value=""],
    input[type="text"],
    input[type="file"],
    input[type="url"] {
        padding-top: 3px !important;
    }
    input[type="search"],
    input[type="text"],
    input[type="url"],
    input[name="login"],
    input[name="password"] {
        background-color: #181818;
        border: 1px solid #212121;
        border-radius: 3px !important;
        box-shadow: 0 1px 2px rgba(0, 0, 0, 0.075) inset !important;
        color: #ededed !important;
        font-size: 12px !important;
        outline: medium none !important;
        height: 18px !important;
        padding-left: 4px !important;
        padding-bottom: 3px !important;
        padding-right: 2px !important;
        margin-right: 15px;
        margin-bottom: 5px;
    }
    input[value=""] {
        background-color: #181818;
        border: 1px solid #212121;
        border-radius: 3px !important;
        box-shadow: 0 1px 2px rgba(0, 0, 0, 0.075) inset !important;
        color: #ededed !important;
        outline: medium none !important;
    }
    input[type="file"] {
        background-color: #181818;
        border: 1px solid #141414;
        border-radius: 3px !important;
        box-shadow: 0 1px 2px rgba(0, 0, 0, 0.075) inset !important;
        color: #ededed !important;
        font-size: 10px !important;
        outline: medium none !important;
        height: 18px !important;
        padding-left: 4px !important;
        padding-bottom: 2px !important;
        padding-right: 2px !important;
        margin-top: 5px;
        margin-right: 15px;
        margin-bottom: 5px;
    }
    input[type="submit"],
    input[type="button"],
    button {
        cursor: pointer;
        background: none repeat scroll 0 0 #181818;
        border: 1px solid #212121;
        color: #c4c4c4;
        border-radius: 3px;
        box-shadow: 0 1px 2px rgba(0, 0, 0, 0.075) inset;
    }
    input.Button {
        position: relative;
        right: 20px !important;
    }
    #search-submit {
        position: relative;
        top: 1px;
        height: 22px;
        width: 22px;
        font-size: 12px !important;
        font-weight: bold !important;
    }
    textarea,
    select {
        padding: 1px 4px 2px 2px;
        box-sizing: border-box;
        background-color: #181818;
        border: 1px solid #141414;
        color: #eee;
    }
    input[type="submit"],
    input[type="button"],
    button,
    select {
        font-size: 12px !important;
    }
    input[value=""]:focus,
    input[type="search"]:focus,
    input[type="text"]:focus,
    input[type="file"]:focus,
    input[type="url"]:focus,
    textarea:focus,
    input[value=""]:hover,
    input[type="search"]:hover,
    input[type="text"]:hover,
    input[type="file"]:hover,
    input[type="url"]:hover,
    textarea:hover,
    input[type="submit"]:hover,
    input[type="button"]:hover,
    button:hover,
    select:hover {
        background-color: #1c1c1c;
        color: #fff;
    }
    input[value=""]:focus,
    input[type="search"]:focus,
    input[type="text"]:focus,
    input[type="file"]:focus,
    input[type="url"]:focus,
    textarea:focus {
        border-color: #444;
    }
    #additional-info-text {
        background-color: #181818;
        padding: 6px 10px 0px 10px;
    }
    #advanced-search {
        margin-top: 5px;
        margin-bottom: 0px;
        padding-bottom: 5px;
    }
    #advanced-search-submit {
        margin-top: 5px;
    }
    a[href="/categories"] {
        display: inline-block;
        margin-top: 2px;
    }
    #show-advanced-search {
        margin-top: 5px;
        font-size: 12px;
    }
    .more {
        border-top-color: #666;
    }
    .TextColor {
        color: #ededed !important;
    }
    label,
    code,
    pre {
        line-height: 30px !important;
    }
    code,
    pre {
        background-color: #181818;
    }
    span[style="color: blue;"] {
        color: #87d7ff !important;
    }
    span[style="color: purple;"] {
        color: #ec009f !important;
    }
    span[style="color: red;"] {
        color: #d75f5f !important;
    }
    span[style="color: yellow;"] {
        color: #ffd787 !important;
    }
    .install {
        padding: 2px 12px 6px 4px;
        background-image: linear-gradient(#2f2f2f, #1c1c1c);
    }
    .install:hover {
        background-image: linear-gradient(#d0d0d0, #e3e3e3);
        color: #1c1c1c;
    }
    #social,
    .ad {
        display: none;
    }
}
