/*
/* Priv8 Dark Compact v1.0
/*
/* Author: Kevin MacMartin - prurigro (https://userstyles.org/users/262769)
/* Priv8 Extension: https://addons.mozilla.org/firefox/addon/priv8 (source: https://github.com/bakulf/priv8)
*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('chrome://priv8') {
    html {
        min-width: 471px !important;
    }
    body {
        background-color: #333 !important;
    }
    body,
    h1,
    #sandboxes-list h2 {
        color: #e3e3e3 !important;
    }
    a {
        color: #0688d4;
    }
    #wrapper {
        width: 95%;
    }
    h1 {
        display: inline-block;
    }
    h1.trans {
        font: 28px Helvetica, arial, freesans, clean, sans-serif, "Segoe UI Emoji", "Segoe UI Symbol";
        font-weight: bold;
    }
    h1 img {
        width: 32px;
        height: 32px;
        position: relative;
        top: 6px;
        right: 6px;
    }
    h2 {
        border-top-right-radius: 5px;
        border-top-left-radius: 5px;
        border-bottom: none;
        padding-top: 8px;
        margin-top: 8px;
        margin-bottom: 0px;
    }
    h2,
    #sandboxes-list ul {
        padding-left: 10px;
        padding-right: 10px;
        background-color: #222222;
    }
    #sandboxes-list ul {
        border-bottom-right-radius: 5px;
        border-bottom-left-radius: 5px;
    }
    #sandboxes-list ul li {
        border-bottom: none;
        margin-top: 0;
        margin-bottom: 0;
        height: 35px;
        min-height: 0;
        display: inline-block;
    }
    input[type="button"] {
        padding: 2px;
        background: none repeat scroll 0 0 #181818;
        border: 1px solid #ccc;
        border-radius: 3px;
        box-shadow: 0 1px 2px rgba(0, 0, 0, 0.075) inset;
        color: #c4c4c4;
        cursor: pointer;
        font: 14px/1.4 Helvetica, arial, freesans, clean, sans-serif, "Segoe UI Emoji", "Segoe UI Symbol";
        font-weight: bold;
    }
    input[type="text"] {
        background-color: #181818;
        color: #f3f3f3;
        border: 1px solid #ccc;
        border-radius: 3px;
        box-shadow: 0 1px 2px rgba(0, 0, 0, 0.075) inset;
        outline: medium none;
        height: 18px;
        padding-top: 1px;
        padding-left: 4px;
        padding-bottom: 3px;
        padding-right: 2px;
        margin-right: 15px;
    }
    input[type="text"]:hover,
    input[type="text"]:focus {
        font-weight: bold;
    }
    input[type="text"]:focus {
        border: 1px solid #444;
    }
    input[type="text"],
    select {
        font-size: 13px;
    }
    select {
        height: 24px;
        padding: 0px 4px 2px 2px;
        box-sizing: border-box;
        background-color: #181818;
        border: 1px solid #ccc;
        color: #eee;
    }
    select option {
        color: #111;
    }
    select option[value="black"],
    select option[value="blue"],
    select option[value="darkblue"],
    select option[value="darkslateblue"],
    select option[value="darkslategray"],
    select option[value="darkslategrey"],
    select option[value="indigo"],
    select option[value="mediumblue"],
    select option[value="midnightblue"],
    select option[value="navy"] {
        color: #f3f3f3;
    }
    #sandboxes-list ul li strong {
        display: none;
    }
    #create {
        position: relative;
        top: 10px;
        right: 10px;
    }
}
