css:
#appTabList td a {background-color:rgba(166, 206, 57, 0.7);border:0;border-radius:0;margin-right:10px;text-shadow:none;padding:10px;padding-left:11px;}
#appTabList td a:hover span {border:0 !important}
#appTabList td a:hover, #appTabList td.active a {background-color:rgba(166, 206, 57, 1);}
.hideoff {display:none;}

#globalNavPageNavArea, #topTabs{height:200px !important;background:none}
.brandingImgWrap {height:150px;}
.brandingImgWrap img{display:none;}
.brandingImgWrap a {background-image: url(https://saxion.nl/wps/saxion_pb/themes/html/saxion_nl/img/main/logo-green-saxion_NEW.png);width:240px;height:90px;margin-left:28px;margin-top:30px;}

#moduleBorder, #moduleBody, .portlet {
border-top-color: #dbebaf !important;
border-left-color: #dbebaf !important;
border-bottom-color: #dbebaf !important;
border-right-color: #dbebaf !important;
background-color: #dbebaf !important;
}
.portlet h2.noTitle {height:34px !important}
#moduleTitle, .portlet h2, .portlet h2 span.reorder {background-color:rgb(166, 206, 57) !important;color:#ffffff !Important;text-shadow:none !important;}

.collapsible table, .collapsible table tbody, .collapsible table td {border:0px !important;}

#globalNavPageContentArea a, #globalNavPageNavArea a, .portlet .subCategories li a, .blockGroups .itemHead, .blockGroups .itemHeadOpen {color:#00853a !important;}
#globalNavPageContentArea li.mainButton > a, #globalNavPageContentArea .secondaryButton > a, #global-nav-link, #appTabList td a{color:#ffffff !important;}

#tab-inner {display:none !important;}

.breadcrumbs, .paneTabs {border:1px solid #ccc;border-radius:3px;}

#quick_links_wrap {top:0 !important;border-radius: 0 0 4px 0}
.global-nav-bar a.logout-link {border-radius: 0 0 0 4px}
#global-nav-link {border-radius: 0 0 4px 4px}

.contentPaneWide, .portalPane, .breadcrumbs {margin: 0 28px;}
.contentPane {margin: 0 28px 0 222px;}
  
.container > .contentBox {border:0 !important;}
.containerPortal {padding:0 !important;}
.portal .contentBox, .actionBar, #topTabs {box-shadow:none;}
.actionBar {border-top:0;border-bottom:1px solid #cccccc;}

#globalNavPageNavArea {background:url('http://www.saxcross.linkhosting.nl/wp-content/uploads/2013/03/slide_update.jpg') center center;background-size:cover;}
.tabWrapper-left {background:rgba(0,0,0,.25); padding:7px 28px;}
#globalNavPageContentArea {padding-top:15px;}
/* i really want this to be global */
