css:
@-moz-document url-prefix("http://www.sweclockers.com"), url-prefix("https://www.sweclockers.com") {
div .hfix > .mainMenu {
  position: fixed;
  z-index: 201;
  top: 0px;
  left: 0px;
  width: 100%;
}

div .header > .main {
  position: fixed;
  z-index: 200;
  top: 18px;
  left: 0px;
  width: 100%;
  /* height: 47px !important; */
  min-height: 47px !important;
}

div .header > .mainMenu > .shadow {
  padding-top: 2px !important;
}

div .hfix > .banner {
  margin-top: 75px !important;
  margin-bottom: 35px !important;
}


div .main > .container > .sections {
  height: 28px;
  min-width: 1200px !important;
}

div .main > .container > .sections > .section {
  margin-top: 2px;
  margin-bottom: 0px;
  height: 35px;
}
div .main > .container > .sections > .profile {
  width: 450px;
  min-width: 450px !important;
}

* .header .profile .option {
  padding: 0px;
}

* .header>.main .profileName {
  margin-bottom: 0px;
}

* .ninjaSearch h5 {
  display: none;
}

* .ninjaSearch .searchField .fieldWrap {
  margin-top: 2px;
}

* .mainMenu .menu li a {
  padding-top: 2px;
  padding-bottom: 2px;
}

* .mainMenu .menu li.isSelected a {
  padding-top: 2px;
  padding-bottom: 3px;
}

* .subMenu li a {
  padding-top: 2px;
  padding-bottom: 2px;
}

* .subMenu li.isSelected a {
  padding-top: 2px;
  padding-bottom: 3px;
}

* .header .logo {
  width: 163px;
  height: 13px;
  background-size: 166px auto !important;
  margin-top: 2px;
}

* .header .ticker {
  margin: 1px 0px;
  font-size: 10px !important;
  line-height: 1;
}




/* New control panel size reduction. Compatible with onsite javascripts. */
* h5.name {
  line-height: 1.0 !important;
}

* .header>.main .section.profile h5 a {
  margin-bottom: 0px !important;
  line-height: 1.0 !important;
}

* .header .profile .option {
  clear: none !important;
}


div .main > .container > .sections > .profile {
  margin-top: 1px !important;
  padding-top: 0px !important;

  -ms-transform: scale(.8,.8);
  -webkit-transform: scale(.8,.8);
  transform: scale(.8,.8);

  -ms-transform-origin: -50% 0% 0px;
  -webkit-transform-origin: -50% 0% 0px;
  transform-origin: 0% 0% 0px;
}


}
