@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("animespirit.ru") {
  body {background: url(http://ya-vidoc3000.narod.ru/anime/6.jpg) no-repeat fixed; -webkit-background-size: cover; background-size: cover;}
  #header, #CollapsiblePanel_LTBanner_NOHIDE, #CollapsiblePanel_Interesnoe, #CollapsiblePanel_LBBanner_NOHIDE, #CollapsiblePanel_Statistics, #CollapsiblePanel_RTBanner_NOHIDE, #CollapsiblePanel_RBBanner_NOHIDE, #CollapsiblePanel_Recommend, #footer, #CollapsiblePanel_Sitenews, #fb-root, .fb_iframe_widget, #CollapsiblePanel_TagsCloud, #CollapsiblePanel_AnimeRadio, #CollapsiblePanel_NarutoTitle, #CollapsiblePanel_HentaiTitle {display: none;}
  #CollapsiblePanel_Profile, #CollapsiblePanel_AnimeRadio, #CollapsiblePanel_TopAnime, #CollapsiblePanel_TagsCloud, #CollapsiblePanel_Sitenews, #CollapsiblePanel_Chat {background-color: #FFE4E1 !important; opacity: 0.7;}
  .search-block, .content-main-block {opacity: 0.7;}
  .block, .block-title, .CollapsiblePanel, .CollapsiblePanelOpen, .CollapsiblePanelTab, .CollapsiblePanelContent {border-radius: 80px;}
  h2 {color: black;}
  .block-title  {background: url(http://ya-vidoc3000.narod.ru/images/na_saity/block_header.gif) no-repeat;}
  .content-block-title {background: url(http://ya-vidoc3000.narod.ru/images/na_saity/block_header.gif)  repeat-x scroll 0% 0% transparent;}
  .search-block {background: url(http://ya-vidoc3000.narod.ru/images/na_saity/block_header.gif)  repeat scroll 0% 0% transparent;}
  .search-block-in {background: none;}
  .content-block-title h2 {background: none;}
  .block-title h3 {background: none;}
  .block-title h2 {background: none;}
  #cboxdiv {opacity: 1;}
  #CollapsiblePanel_TopAnime:hover, #CollapsiblePanel_Profile:hover, #CollapsiblePanel_Chat:hover, #CollapsiblePanel_AnimeRadio:hover, .search-block:hover, .content-main-block:hover {opacity: 1.0;}
  .sub, #nav {background-color: red !important; border: 1px solid red;}
  #nav li ul.sub li a.fly {background: url("http://ya-vidoc3000.narod.ru/images/na_saity/arrow.gif") no-repeat scroll 80px 6px red;}
  #nav li:hover ul.sub {border: 1px solid red; background-color: red;}
  #nav li a.top_link {color: red;}
  .ctitle {background: url(http://ya-vidoc3000.narod.ru/images/na_saity/block_header.gif)  repeat scroll 0% 0% transparent;}
  #nav li:hover ul.sub li a:hover {background-color: red; border: 1px solid red;}
  #nav li:hover ul.sub li a {background-color: red; border: 1px solid red;}
  #nav li:hover li:hover ul, #nav li:hover li:hover li:hover ul, #nav li:hover li:hover li:hover li:hover ul, #nav li:hover li:hover li:hover li:hover li:hover ul {border: 1px red; background: none !important;}
  #nav {background:none; background-color: black !important;}
  #nav li:hover a.top_link span, #nav li.iehover a.top_link span {background: none; background-color: black !important;}
  #nav li:hover a.top_link, #nav li.iehover a.top_link {background: none !important;}
  #nav li a.top_link {background: none !important;}
  #nav li a.top_link span {background: none !important;}
  #nav li:hover a.top_link span.down, #nav li.iehover a.top_link span.down {background: none!important;}
  #nav li a.top_link span.down {background: none !important;}
  #CollapsiblePanel_Chat, #CollapsiblePanel_Profile {position: fixed !important;}
  .content-main-block {background: none;}
  .a_block_12, .a_block_63m .a_block_22, .a_block_32, .a_block_42, .a_block_52, .a_block_61, .a_block_63, .a_block_72, .a_block_82  {background: none;}
}
