body {
    background-color: #D0D0D0;
    font-family: 'Josefin Sans';

	}

h1, h2	{
	color:green;
  	font-family: 'Oswald', sans-serif;

}

a:visited { 
color: darkblue;

}
#general1 {
  	margin-left: auto;
    margin-right: auto;
  
  }
#general2 {
  	margin-left: auto;
    margin-right: auto;
  max-width: 60em;
  }

#text {
  	padding: 2em;
  background-color: #F0F0F0;

    border-radius: 15px;
  }

#text ul li {
  width: 15em;
	display: inline-block;
  text-align:center;
	}

#text > ul li:nth-child(1) {
  width: auto;

  text-align:left;
	}

#content {
	border: 0.5em solid blue;
    border-radius: 15px;
	}

ul li{
    border: 2px solid green;
    border-radius: 10px;
    padding: 0.5em;
    background-color: lightblue;
list-style-type: none;

}


@media screen and (max-width: 50em) {
    body {
        background-color: lightblue;    
    }
  #content {
	border: 0;
	}
ul li{
    border: 2px solid green;
    border-radius: 1em;
    padding: 0.5em;
    background-color: lightblue;
list-style-type: none;
}
 
 
#text {
  	padding: 0.2em;
  background-color: #F0F0F0;

    border-radius: 1em;
  }
  
  #text ul {
  padding: 0px;
    }
  #text ul li {
  margin: 1em;
  
  }
}

@media screen and (min-width: 50em) {
    #general2 {
max-width: 90%;
}
  #text {
  max-width: 100%;
  }

}
