body {background-color: #e5e7e9;
	}
 
h1 {font-family: 'Ek Mukta', sans-serif; color:#ffffff;
    float:left;}

.red{
color:#ffffff;
}

h2, h3, p {font-family: 'Ek Mukta', sans-serif; color:#7F7C78;
           clear:left;}

p {font-family: 'Ek Mukta', sans-serif;font-size:1.2em;color:#7F7C78;
    padding: 0.5em;
    }


