    background-color: white;
	font-family: 'Ek Mukta', sans-serif;font-size:1.2em; color:#7F7C78;
  	margin:2em 0 0.5em;
  	padding:2em;
    }

ul li a {font-family: 'Ek Mukta', sans-serif; color:#00688a;
  	margin:2em 0 0.5em;
  	padding:0;
}

a {font-family: 'Ek Mukta', sans-serif; color:#00688a;
  	
}

@media print{
  h1 {font-family: 'Ek Mukta', sans-serif;font-size:2em; color:#403E3C;}
  h2 {font-family: 'Ek Mukta', sans-serif; font-size:1.5em;}
  h2.koutek{font-family: 'Ek Mukta', sans-serif; font-size:1.5em;}
  h2.jaroslav_pavelka{font-family: 'Ek Mukta', sans-serif; font-size:1.5em;}
  h2.o-webu{font-family: 'Ek Mukta', sans-serif; font-size:1em;}
  ul li {font-family: 'Ek Mukta', sans-serif;font-size:1em;}
  ul li a {font-family: 'Ek Mukta', sans-serif;font-size:1em;}
  a {font-family: 'Ek Mukta', sans-serif;font-size:1em;}
  p {font-family: 'Ek Mukta', sans-serif;font-size:1em;}
  p.socialIcons{font-family:'Ek Mukta', sans-serif;font-size:1em; text-align:center;}
}
