css:
@-moz-document url-prefix("https://identityprotect.experian.co.uk") {
#tabPanel a, #tabPanel a:visited, a.extraAddButton, a.tabRefButton {
  background: #cc3333;
  background: -moz-linear-gradient(top,  #dd5555 0%, #aa2222 100%); /* FF3.6+ */
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#dd5555), color-stop(100%,#aa2222)); /* Chrome,Safari4+ */
	background: -webkit-linear-gradient(top,  #dd5555 0%,#aa2222 100%); /* Chrome10+,Safari5.1+ */
	background: -o-linear-gradient(top,  #dd5555 0%,#aa2222 100%); /* Opera 11.10+ */
	background: -ms-linear-gradient(top,  #dd5555 0%,#aa2222 100%); /* IE10+ */
	background: linear-gradient(to bottom,  #dd5555 0%,#aa2222 100%); /* W3C */
}
 a.extraAddButton, a.tabRefButton {
  border-radius: 5px;
}
#tabPanel a:hover, #tabPanel td.on a, .dpProtectionInfoBoxHeader span, .boxBody b, .contactUsBody b, .dpx-help-category-title, .accountSettings h1, .accountSettings h2 {
  color: #cc3333 !important;
}
#tabPanel a {
  position: relative;
  z-index: 100;
}
#mainbody, #lhBoxWrapper, #dpProtectionInfoBox, .dpProtectionInfoBoxHeader, #tabPanel td.on a, #tabPanel a:hover {
  background: #eee !important;
}
#tabPanel td a {
  border-top-left-radius: 5px;
  border-top-right-radius: 5px;
  border-color: #D7D7D7;
  border-style: solid;
  border-width: 1px 1px 0 1px;
}
#mainbody {
  border: 1px solid #D7D7D7;
}
div.t-data-grid, .dataTable .even td, .even, .dataTable .odd td, .odd {
  background-color: #FFFFFF;
}
.miscPanel, div#mainbody div#helpBag.dpx-lightbox-scrollablecontent, .dpx-help-title-even a, .dpx-help-title-odd a, .dpx-help-title-even a:hover, .dpx-help-title-odd a:hover {
  background: #FFFFFF;
}
.miscPanel, div#mainbody div#helpBag.dpx-lightbox-scrollablecontent {
  border: 1px solid #ccc;
}
.dpx-help-category {
  border-top: 2px solid white;
}
}
