css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.manjaro.org") {
  
 *{font-family: monospace !important;font-size: 1em !important;}
    
 /* remove stuff (social,signature,etc..) */
 #quicknav, #notices, .signature, .attachments {display: none;}
  
 /* color links, forum+background */
 a:link, a:visited {color: #5A885F !important;padding-left: 3px; padding-right: 3px;} 
 a:hover {color: #dadada !important;cursor: pointer;font-size: 1.2em;background: #4F8567;font-weight:normal !important;}
 body, td, th, tr {color: #959595 !important;}
 body {background-color: #1e1e1f;background-image:url('data:image/svg+xml;base64,');background-attachment: fixed;}

 /* resize */
 #main-content {background: transparent !important;width:1100px !important;margin: auto !important;}
 table.table_list tbody.content td.lastpost {text-align: right;width:34%;}
 table.table_list tbody.content td.lastpost {text-align: center;}
 table.table_list tbody.content td.stats {width: 10%;}
  
 /* header panel */ 
 #navbar {background: #171717;height: 30px;}
 #topnav li ul {background: none repeat scroll 0 0 #171717;width: 200px;}
 #topnav li a.active {background: none repeat scroll 0 0 #466752 !important;color:#272727!important;line-height: 30px;}  
 #topnav li a {height: 30px;line-height: 30px;}
 #topnav li:hover > a {background: #171717 !important;color:#fff !important;}
 #topnav li:hover ul {top: 30px;}
 #toolbar {background: #466752 !important;border-bottom: 1px solid #262626 !important;height: 5px;}
 #account li a:hover {background: none repeat scroll 0% 0% #272727;color:#fff !important;}
 #account li a {background: none repeat scroll 0 0 #466752;color:#fff !important; font-weight: bold !important;}
 #account li {height: 30px;line-height: 30px;margin: 5px 0 0 0px;}
 #account li.signin a {background: none repeat scroll 0 0 #466752;color:#fff !important;font-weight: bold !important;}
 #account li.signin a:hover {background: none repeat scroll 0% 0% #272727;color:#fff !important;}
  
 #logobutton a {display: none !important;}
 
   /* titlebar */
  span.lowerframe, span.upperframe span, div.title_barIC, div.cat_bar, h4.catbg, h4.catbg2, h3.catbg, h3.catbg2, .table_list tbody.header  td.catbg {background: none repeat scroll 0 0 #466752!important;}
  .table_list .section-header td, .table_grid .section-header th, .table_grid .section-header a, .calendar_table tr th {background: #627D6C !important;color: #cccccc !important;}
  .title_bar {display: none !important;}
  .table_frame .table_list td.icon, .table_frame .table_list td.info, .table_frame .table_list td.stats {border-right: none !important;}
  h3.catbg {color: #cccccc !important;}
  
  
 .inner {border-top: 0px solid #c7c7c7;}
 span, span.topslice, span.botslice {height: 0px !important;background: none !important;}
 .topic_table td.locked_sticky2, td.icon2.stickybg, td.lastpost.stickybg, td.stats.stickybg2, td.subject.stickybg2 {background: #2d2d2d;}
 div.roundframe div.cat_bar h3.catbg { background: #505050;}
 .table_grid .stats span, .table_grid .lastpost span {color: #8C8C8C ;}
 .smalltext, tr.smalltext th {color: #A3BCA3;font-weight: normal;}
  
  
  /* code box */
  .geshi, code.bbc_code, blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote {background: #2d2d2d;color:#627562;border: none;border-radius: 2px;}  
  
  /* recent post+stats */
  html body div#main-content div.wrapper div.roundframe div.innerframe div.cat_bar  {display:none}
  html body div#main-content div.wrapper div.roundframe div.innerframe div#upshrinkHeaderIC.infocenter div.title_barIC {height: 20px}
  div.title_barIC {padding-left: 0px}
  h4.titlebg img.icon, h3.titlebg img.icon {display:none}
  html body div#main-content div.wrapper div.roundframe div.innerframe div#upshrinkHeaderIC.infocenter p,
  html body div#main-content div.wrapper div.roundframe div.innerframe div#upshrinkHeaderIC.infocenter p.inline.smalltext,
  html body div#main-content div.wrapper div.roundframe div.innerframe div#upshrinkHeaderIC.infocenter p.inline.stats,
  html body div#main-content div.wrapper div.roundframe div.innerframe div#upshrinkHeaderIC.infocenter p.last.smalltext,
  html body div#main-content div.wrapper div.roundframe div.innerframe div#upshrinkHeaderIC.infocenter div.title_barIC,
  html body div#main-content div.wrapper div.roundframe div.innerframe div#upshrinkHeaderIC.infocenter p.inline.stats,
  html body div#main-content div.wrapper div#boardindex_table table.table_list tbody#category_4_boards.content tr.section-header,
  html body div#main-content div.wrapper div#boardindex_table table.table_list tbody#category_8_boards.content tr.section-header,
  html body div#main-content div.wrapper div#boardindex_table table.table_list tbody#category_6_boards.content tr.section-header,
  html body div#main-content div.wrapper div#boardindex_table table.table_list tbody#category_9_boards.content tr.section-header,
  html body div#main-content div.wrapper div#boardindex_table table.table_list tbody#category_10_boards.content tr.section-header,
  html body div#main-content div.wrapper div#boardindex_table table.table_list tbody#category_3_boards.content tr.section-header,
  html body div#main-content div.wrapper div#boardindex_table table.table_list tbody#category_2_boards.content tr.section-header
  {display: none !important;}
  
  /* resize title bars */
  div.cat_bar {height: 20px;overflow: hidden;margin-bottom: 0px;}
  h3.catbg, h3.catbg2, h3.titlebg, h4.titlebg, h4.catbg {line-height: 20px;font-size: 1em;font-weight: bold;}
  
  html body div#main-content div.wrapper div#board_4_childboards.tborder.childboards div.table_frame table.table_list tbody#board_6_children.content tr.section-header,
  html body div#main-content div.wrapper div#board_8_childboards.tborder.childboards div.table_frame table.table_list tbody#board_8_children.content tr.section-header,
  html body div#main-content div.wrapper div#board_6_childboards.tborder.childboards div.table_frame table.table_list tbody#board_6_children.content tr.section-header,
  html body div#main-content div.wrapper div#board_9_childboards.tborder.childboards div.table_frame table.table_list tbody#board_9_children.content tr.section-header,
  html body div#main-content div.wrapper div#board_10_childboards.tborder.childboards div.table_frame table.table_list tbody#board_10_children.content tr.section-header,
  html body div#main-content div.wrapper div#board_3_childboards.tborder.childboards div.table_frame table.table_list tbody#board_3_children.content tr.section-header,
  html body div#main-content div.wrapper div#board_2_childboards.tborder.childboards div.table_frame table.table_list tbody#board_2_children.content tr.section-header,
  html body div#main-content div.wrapper div#messageindex.tborder.topic_table table.table_grid thead tr.section-header
  {display: none !important;}
  
  html body div#main-content div.wrapper div#messageindex.tborder.topic_table table.table_grid tbody tr.titlebg {display: none !important;}
   
  div.title_barIC:nth-child(5) > h4:nth-child(1) > span:nth-child(1), div.title_barIC:nth-child(3) > h4:nth-child(1) > span:nth-child(1), div.title_barIC:nth-child(1) > h4:nth-child(1) > span:nth-child(1) {color:#cccccc;}
  #upshrinkHeaderIC {background: #222222;}
  
  /* jump to */
  #display_jump_to, #topic_icons .description, .plainbox {background: none repeat scroll 0 0 #222;border:none;border-radius: 1px;color: #5f5f5f;}
    
  /* legend */
  .description, .description_board, .plainbox {background: none repeat scroll 0 0 #3c3c3c;}

  /* date + icon */
  html body div#main-content div.wrapper div#forumposts form#quickModForm div.windowbg2 div.post_wrapper div.postarea div.flow_hidden div.keyinfo div.messageicon img,
  html body div#main-content div.wrapper div#forumposts form#quickModForm div.windowbg div.post_wrapper div.postarea div.flow_hidden div.keyinfo div.messageicon img,
  html body div#main-content div.wrapper div#forumposts form#quickModForm div.windowbg2 div.post_wrapper div.postarea div.flow_hidden div.keyinfo h5,
  html body div#main-content div.wrapper div#forumposts form#quickModForm div.windowbg div.post_wrapper div.postarea div.flow_hidden div.keyinfo h5 
  {display: none !important;}  
  
  /* link online */
  html body div#main-content div.wrapper div#forumposts form#quickModForm div.windowbg div.post_wrapper div.moderatorbar div.smalltext.reportlinks,
  html body div#main-content div.wrapper div#forumposts form#quickModForm div.windowbg2 div.post_wrapper div.moderatorbar div.smalltext.reportlinks
  {display: none !important;}
  
  /* info poster */
  .poster h4, .poster ul {font-style: normal;font-weight: normal;color: #535353;}
  .poster li.custom, .poster li.blurb, .poster li.profile, .poster li.avatar, .poster li.stars, li.im_icons ul {display: none !important;}
  
  /* boutton print */
  .buttonlist li:hover > a {background: none repeat scroll 0 0 #3f3f3f!important;color:#fff!important;}
    
  /* button quote */
  ul.quickbuttons a:hover {background: #627D6C!important;color: #dddddd !important;}
  ul.quickbuttons a {background:#555!important; color: #222 !important;}
  
  /* button bar */
  .buttonlist li a {background: none repeat scroll 0 0 #313131 !important;}
  .buttonlist li:hover > a {background: none repeat scroll 0 0 #222222 !important; }
    
  /* loggin*/
  .roundframe, span.upperframe {background: none repeat scroll 0 0 #222;border:none;padding: 0px;}

  /*rating post removed*/
  html body div#main-content div.wrapper div#forumposts form#quickModForm div.title_bar h3.titlebg strong,
  html body div#main-content div.wrapper div#forumposts form#quickModForm div.windowbg div.post_wrapper div.moderatorbar div div span strong,
  html body div#main-content div.wrapper div#forumposts form#quickModForm div.windowbg2 div.post_wrapper div.moderatorbar div div, 
  html body div#main-content div.wrapper div#forumposts form#quickModForm div.windowbg div.post_wrapper div.moderatorbar div div
   {display: none !important;}
  
  /* box */ 
  .button_submit, .button_reset, input, button, select, textarea {background: none repeat scroll 0 0 #2d2d2d;border: 1px solid #272727;color: gray;}
  .button_submit:hover, .button_reset:hover {background: none repeat scroll 0 0 #3f3f3f!important;color:#fff!important;border:1px solid #272727}
  
  /* buton quick reply */
  h3.catbg a:link, h3.catbg a:visited, h4.catbg a:link, h4.catbg a:visited, h3.catbg, .table_list tbody.header td, .table_list tbody.header td a {color: #272727 !important;}
  
  /* News panel removed */
  html body div#main-content div.wrapper div#newsfader {display:none !important;}
  
  /* Warning window */
  .information {background: none repeat scroll 0 0 #222;color: orange;}
  
  /* count post */  
  .table_list .stats span {color: #e8e8e8;}
  #footer {background: #222;border-top:5px;border-color:#466752 ;}
  
  /* forum transparency */
   #preview_body, .windowbg, .windowbg2{background-color: rgba(39, 39, 39, 0.5) !important;border-bottom:1px solid #3c3c3c !important;}
  .table_list .content td, .table_grid tbody td, #forumposts .windowbg, #forumposts .windowbg2 { border-bottom: 1px solid rgba(45, 45, 45, 0.8) !important;}
  table.table_list tbody.content td.icon, table.table_list tbody.content td.lastpost {background: transparent !important;}

  /* images post */
  #forumposts h3 img {margin: 0 0.5em 0 0;}
}
