/* COLORES POR DIAS LABORABLES */

@-moz-document
    url-prefix("https://www.google.com/calendar")
{   

    /*Separate color by date, Start on Monday*/
    .month-row>.st-bg-table>tbody>tr>td:nth-of-type(1),
  .month-row>.st-bg-table>tbody>tr>td:nth-of-type(2),
  .month-row>.st-bg-table>tbody>tr>td:nth-of-type(3),
  .month-row>.st-bg-table>tbody>tr>td:nth-of-type(4),
  .month-row>.st-bg-table>tbody>tr>td:nth-of-type(5){background-color:#fff;}
  .month-row>.st-bg-table>tbody>tr>td:nth-of-type(6){background-color:#F4FAFF;}  /*SABADOS*/
  .month-row>.st-bg-table>tbody>tr>td:nth-of-type(7){background-color:#F6f4f4;}  /*DOMINGOS*/
  .month-row>.st-grid>tbody>tr>.st-dtitle{background-color: transparent !important;color:#696969 !important;padding:2px 7px !important;}
  .month-row>.st-grid>tbody>tr>.st-dtitle-nonmonth{background-color:#696969 !important;color:#fff !important;padding:2px 7px !important;}
  .st-bg-today{
    background-color: #fff8dc !important;
  }
  
  
  
  #mothertable .st-dtitle-today, #container .st-dtitle-today, .st-dtitle-today, .wk-full-mode .wk-today {
    background-color: #FD8 !important;
    color: #F70 !important;
  }
  .st-bg-today, .st-bg-td-first, .st-bg-td-last, .tg-today {
    background-color: #FFB !important;
  }
  .lv-today .lv-datecell {
    background-color: #FD8 !important;
    border-right: 1px solid #FD8 !important;
  }
  .lv-today .lv-eventcell {
    background-color: #FFB !important;
  }
  .dp-today {
    background-color: #FFB !important;
    border-color: #FFB !important;
    color: #F70 !important;
  }
  .dp-today-selected {
    background-color: #FFB !important;
    border-color: #FD8 !important;
    color: #F70 !important;
  }

  
  
  

}

