	/* Reklama */
      #reklama {
        position:relative;
        width:500px;
        height:20px;
				top: 3px;
        line-height:20px;
        margin:0 auto;
        border:2px solid #013953;
				border-radius: 10px;
        font-size:8pt;
        background-color:#0066cc;
        overflow:hidden;
      }
			#reklama a {
				text-decoration: none;
				color: #FFFFFF
			}

      #reklama > p {
        position:absolute;
        margin:0;
        white-space:nowrap;
        animation:przesun 30s linear infinite;
        -webkit-animation:przesun 30s linear infinite; /* dla Google Chrome, Safari, Opera 15+ */
				color: #a6b8cb;
      }

      #reklama > p:hover {
        animation-play-state:paused;
        -webkit-animation-play-state:paused;
      }
	  
      /* ------------ANIMACJA-PRZESUN------------ */
      @keyframes przesun
      {
        100% { transform:translateX(0); padding-left:110%; }
        0% { transform:translateX(-100%); padding-left:110%; }
      }

      @-webkit-keyframes przesun /* dla Google Chrome, Safari, Opera 15+ */
      {
        0% { -webkit-transform:translateX(0); padding-left:110%; }
        100% { -webkit-transform:translateX(-100%); padding-left:110%; }
      }
  
