css:
@-moz-document domain("schoolloop.com") {
/* 
 * Name: SchoolLoop Enhancement
 * Title: SchoolLoop Enhancement
 * Author:  Max Pereira
 * Version: 0.2
 *
 */

/* Hide Unnecessary Elements */

.nav_employee a.trigger,
.module_col,
.sub_header,
.nav_list_hori,
a.icon_help2,
.cal_item_table .width_r_student,
a.icon_print
{
	display: none;
}

/* Change Background Color */
.highlight,
.hub_general tbody tr.highlight td
{
	background-color: #FAFAFA;
}

/* Left Align 'No Grades Published' Text */
.label_light { float: left; width: 300px; }

/* Remove Orange Border on Tabs */
.header_tabs ul li.current a { border: 1px #FAFAFA; }

/* Center bottom orange footer when accordion content expanded */
.ajax_accordion_content, .cal_Detail_holder, .prog_Detail_holder, .curAss_Detail_holder { background: url(https://cdn.schoolloop.com/1501091742/img/accordion_content_bottom.png) bottom no-repeat; }

/* Remove Orange Bottom Border */
.header_tabs ul li a,
.header_tabs
{
	border-bottom: 0px #FAFAFA;
}

/* 'Due' and 'New' images have rounded borders */
img {
  border:1px solid #fff;
  -webkit-border-radius: 20px;
  -moz-border-radius: 20px;
  border-radius: 20px;
}

/* Fill the Screen with the Useful Information */
.home_left { width: 100%; }

/* Pretty Up a Bit */
.clear { padding-bottom: 2px; }

/* Make 'New LoopMail' Button Visible and Nicer */
#container_page { position: relative; }
.alert_loopmail { position:absolute; top:117px; right: 12px; float: right; }
.alert_loopmail .alert_content { display: none; }

/* Change Page Background Color */
body { background: #FAFAFA; }
}
