css:
@-moz-document domain("facebook.com") {

#pagefooter, 
#dropmenu_container, 
#fb_menubar { max-width: 100% !important; }
.feed_item, #page_height { max-width: 98% !important; }
.UIFullPage_Container, 
.UITwoColumnLayout_Container, 
.UITwoColumnLayout_FullPage { max-width: 99% !important; }
.UITwoColumnLayout_Content { max-width: 70% !important; }
.UITwoColumnLayout_NarrowContent { max-width: 30% !important; }

#fbnew_preview_bar, 
.social_ad, 
.social_ad_no_full_clickable, 
.app_story { display: none !important; }
}
