css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.ycombinator.com") {
/*
*
*	HN+ for Chrome v1.5
*	by @jarques
*       modified by @thinkaliker for use with Stylish
*
*/
    
body {
    font-family: "Helvetica" !important;
	background: #fff;
	margin: 0;
}
body center {
	text-align: left;
}
body center table {
	background: #fff;
	width: 980px;
	margin: 0 auto;
  height: 86px;
}
body center table table {
	box-shadow: none;
	padding: 0;
	background: none
}
  
table {
    position: relative;
}
table, td {
  background: none !important;
}
table tr {
  position: relative;
}
.title {
	color: #242222;
	font-size: 18px !important;
	line-height: 18px;
	padding-top: 10px;
}
a:link {
	color: #202020 !important;
	text-decoration: none;
}
a:hover {
	color: #dd4b39 !important;
}
input[type="submit"] {
  font-family: "Helvetica";
	font-size: 13px;
	color: white;
	background: #4D90FE;
	border: 1px solid #4079D6;
	padding: 5px 10px;
	text-transform: capitalize;
	border-radius: 2px;
}
input[type="submit"]:hover {
	background: #3C89ED;
}
  
input[type="submit"]:active {
  font-family: "Helvetica";
	background: #2b78DC;
}
input[type="text"] {
    border: 1px solid #c6cfd6;
    padding: 4px;
	font-size: 12px !important;
}
.title a {
    border-left: 2px solid #fff;
    padding-left: 3px;
    margin-left: -5px;
    -webkit-transition: border .2s ease-in-out;
}
.title a.on_story {
    border-left: 2px solid #3986f8 !important;
}
td.comment-container {
  padding-left: 8px;
  border-left: 2px solid #fff;
}
td.on_comment {
  background-color: #F5F8FC !important;
  border-left: 2px solid #DEE9FA;
}
.title a:visited {opacity: .5;}
.subtext {
	color: #2F2F2F;
	font-size: 9px;
	padding-bottom: 15px;
}
.subtext a, .comhead a {
	color: #2F2F2F !important;
    font-size: 9px !important;
}
.comhead {
	color: #FF5555 !important;
	font-size: 10px !important;
	margin-left: 5px;
}
.title:first-child {
	font-size: 12px !important;
	padding-left: 5px !important;
}
html body center table tbody tr td table tbody tr td center a img {
	padding-top: 3px;
	padding-right: 3px;
}
html body center table tbody tr:first-child td table {
	background: #fff;
	padding: 0 0 5px 0;
	color: #b8b8b8 !important;
}
html body center table tbody tr:first-child td table a {
	color: #4D4D4D !important;
}
.yclinks {
	font-size: 10px;
    color: #b8b8b8 !important;
}
.yclinks {
	display: block;
	padding: 10px;
}
.yclinks a {
	color: #b8b8b8 !important;
}
  a {
  color: #FF8A00 !important;
}
.pagetop, .pagetop a {
    /* font-size: 10px; */
    color: #000000 !important;
}
.pagetop b a {
	display: none;
	font-size: 11px;
	color: #000000 !important;
	text-transform: lowercase;
}
textarea {
   font-family: "Helvetica" !important;
    border: 1px solid #c6cfd6 !important;
    padding: 4px !important;
	  font-size: 12px !important;
    width: 100%;
}
input[name="q"] {
    position: absolute;
    top: 34px;
    left: 450px;
    width: 270px;
    border-radius: 2px;
    color: #c1c6cb !important;
}
input[name="q"]:focus {
    color: #3b3e40 !important;
}
form[action="http://www.hnsearch.com/search#request/all"] {
    color: #222222;
}
html body center table tbody tr td table tbody tr td table {
  background: none !important
}
.default {
  padding-bottom: 10px;border-bottom: 1px dotted #d8d8d8;
}
.default a:link, .default a:visited {
  color: #828282 !important;
}
.comment {
  color: #373736;
  font-size: 13px !important;
  line-height: 20px;
}
.comment code {
  color: #373736;
  font-size: 13px !important;
  line-height: 20px;
}
.comment p {
  font-size: 13px !important;
  margin-top: 15px !important;
  color: #434343;
  line-height: 20px;
}
.comment  a {
  color: #109D59 !important;
}
form textarea {
  background: #fff;
  border: 1px solid #c9c9c7;
  font-size: 14px;
  padding: 10px;
  height:80px;
  overflow: auto;
}
html body center table tbody tr td table tbody tr td a img[src="http://ycombinator.com/images/y18.gif"], html body center table tbody tr td table tbody tr td a img[src="/sslyc/images/y18.gif"] {
	height: 0 !important;
	width: 0 !important;
	/* these numbers match the new image's dimensions */
	padding-left: 36px !important;
	padding-top: 36px !important;
	background: #FFFFFF !important;
}
/* Submit Page */
form[action="/r"] {
	width: 400px;
	margin: 40px;
}
form[action="/r"] table {
	width: 400px;
}
form[action="/r"] input[type="text"], form[action="/r"] textarea {
	width: 380px;
	padding: 5px;
	margin: 5px 0;
}

/* Expired Link Page */
.expired-link {
	padding-top: 150px;
	text-align: center;
	width: 100%;
	color: #a3a3a3 !important;
	font-size: 18px;
}
  
 
.score {
  color: #0099CC !important;
}
  
.votearrow {
  background: url("https://cdn3.iconfinder.com/data/icons/faticons/32/arrow-up-01-128.png");
  background-repeat: no-repeat;
  background-size: 10px;
}
 
}
