@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("e.mail.ru") {
   #theme, .minwidth, .theme {
   background: url(http://ya-vidoc3000.narod.ru/anime/10.jpg) fixed;
    -webkit-background-size: cover;
    background-size: cover;
   }
  .portal-banner, .b-slot_banner, .b-datalist__head, .js-banner, .pm-toolbar__banner__content__text, .footer {
    display: none;
  }
  body {
    background: none;
  }
  #LEGO {
    background: none;
  }
  .b-layout, .b-toolbar, #portal-menu {
    opacity: 0.7;
  }
  .b-toolbar:hover {
        opacity: 1.0;
      }
  .b-layout__col:hover {
        opacity: 1.0;
      }
  #b-letters:hover {
        opacity: 1.0;
      }
  .b-datalists:hover {
        opacity: 1.0;
      }
  .b-datalist_letters:hover {
        opacity: 1.0;
      }
  .w-pm-menu {
    background-color: black;
  }
  #portal-menu {
    background: none;
  }
  #b-nav_folders:hover {
        opacity: 1.0;
      }
   .theme__top, .theme__bottom, .theme__bottom-center, .theme__bottom-left, .theme__bottom-right, .b-sticky, .theme, .w-pm-menu__shadow, .pm-css-checker, .w-pm-menu__shadow, .pm-menu, .theme__left, .theme__right, .theme__top-center, .theme__left-center, .theme__right-center, .theme__center, .theme__top-left, .theme__top-right {
     background: none;
   }
  .b-datalist__item__link, .b-datalist__item__link:hover, .b-datalist__item__attachment__link, .b-datalist__item__attachment__link:hover, .b-datalist__item__filename__link, .b-datalist__item__filename__link:hover, .b-datalist__item__download__link, .b-datalist__item__download__link:hover {
    color: red;
  }
  .b-datalist__item__panel:hover {
    background-color: black;
  }
  .pm-toolbar__search__container {
    background: none;
  }
  #portal-headline {
    opacity: 0.7;
  }
}
