css:
@-moz-document domain("lichess.org") {
body.light{
color: #000000 !important;
  background-color: #DBC73C !important;
  background-image: -webkit-linear-gradient(top,#dbc73c 0%,#dbc73c 116px);
  font: 13px 'Open Sans','Lucida Grande','Lucida Sans Unicode',Geneva,Verdana,Sans-Serif;
}

body.dark{
color:#877F6D;
background-image: -webkit-linear-gradient(top,#877F6D 0%,#877F6D 116px);

}
body.dark #top .language_links button.accepted, body.dark #lichess_forum div.post .message, body.dark #lichess_blog, body.dark #lichess_message div.thread_message div.thread_message_body, body.dark form.translation_form div.message label, body.dark div.user_show div.content_box_top>span strong
{color:black;}



body.dark #chat .messages, body.dark div.side_box, body.dark div.table, body.dark div.table_wrap>div.clock>div.time, body.dark #friend_box .content a:hover, body.dark #footer_wrap, body.dark div.undertable_inner, body.dark #tv_history div.content, body.dark div.table .loader:before, body.dark div.table .loader:after, body.dark div.chat_menu>a.active, body.dark div.chat_panels>.notes{

background-color: #BEC2C7 !important;
}

body.dark div.table_wrap>div.clock.running>div.time{

background:#C1C5C9;
  color:#000000;
}

body.dark a, body.dark a:visited, body.dark #powerTip>.actions a, body.dark .button{

color:#000000;
}

body.dark #top a.signin, body.dark #top a.toggle:hover, body.dark #top a.goto_nav.current, body.dark #top a.goto_nav:hover, body.dark #sound_state:hover{

color:orange;
}



body.dark .crosstable th, body.dark #chat div.top, body.dark div.side_box div.top, body.dark #friend_box .title, body.dark div.undertable_top, body.dark .button, body.dark .button:visited, body.dark .ui-state-default, body.dark #translation_call, body.dark #notifications>div, body.dark div.vstext, body.dark div.user_show div.content_box_inter.tabs, body.dark #qa .answers-header, body.dark table.slist thead{

background:linear-gradient(to bottom,rgba(255, 255, 255, 1) 0%,rgba(77, 64, 64, 1) 100%);

text-shadow:0px 1px 0px #CFBABA;
}

body.dark{

color: black;
 
  
}

#hooks wrap{

display: table-cell;
min-width: 512px;
width: 512px;
height: 512px;
max-height: 512px;
overflow: hidden;
border: 1px solid #BE2020;

background-size: 100% 100%;
}


body.dark #hooks_wrap, body.dark #hooks_wrap div.tabs a:hover, body.dark #hooks_wrap div.tabs a.active{

background-color: #E2D8D8;


}


body.dark #timeline a, body.dark #chat span, body.dark #chat a.user_link, body.dark div.undertable a.user_link, body.dark #tv_history a.user_link, body.dark #timeline a.user_link, body.dark #team .forum a.user_link, body.dark div.user_show div.content_box_top>span, body.dark span.progress>.zero, body.dark div.undertable_top span.title{

color:yellow;

}



body.dark div.game_row:nth-child(odd), body.dark #lichess_forum table.forum_table tr:nth-child(odd), body.dark #lichess_message tr:nth-child(even), body.dark div.content_box_inter, body.dark table.slist tbody tr:nth-child(even), body.dark #team .forum li:nth-child(odd), body.dark table.translations tbody tr:nth-child(odd), body.dark form.translation_form div.message:nth-child(even), body.dark div.content_box table.datatable tr:nth-child(odd), body.dark div.game_config .optional_config, body.dark div.game_config .ratings, body.dark div.search_status, body.dark #friend_box, body.dark #powerTip{

background:#cbcbcb;

}

body.dark .lichess_ground .replay .turn .index, body.dark .lichess_ground .comment, body.dark .lichess_ground .variation, body.dark .lichess_ground .replay a.move:hover, body.dark .lichess_ground .replay td.move:hover, body.dark #top .dropdown, body.dark #top .shown a.toggle, body.dark div.analysis_menu>a, body.dark div.lichess_overboard, body.dark #tv_history tr:hover td, body.dark div.content_box, body.dark div.content_box_inter a.active, body.dark #site_header div.side_menu a.active, body.dark #tournament_side, body.dark table.translations tbody tr, body.dark form.translation_form div.message, body.dark #lichess_forum table.forum_table thead tr:nth-child(odd), body.dark div.lichess_overboard .loader:before, body.dark div.lichess_overboard .loader:after, body.dark div.content_box .loader:before, body.dark div.content_box .loader:after{

background:#Cbcbcb;
}

.wood2 .is2d .cg-board, #top div.color_demo.wood2{
background-image: url(http://s6.postimg.cc/rffhuf6wx/Fritz.jpg);


}

.canvas .is2d .cg-board, #top div.color_demo.canvas{

background-image: url(http://lichess1.org/assets/images/board/yellow.png) ;
}

.leather .is2d .cg-board, #top div.color_demo.leather{

background-image: url(http://s6.postimg.cc/5uaf6ta69/maple.jpg);
}


}
