@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.mz-web.de")
{
  body {
   color: #000;
   background-color: #0C090A; 
   
 }

 h2 a {
   color: #9f9f9f;
 }
  
 h3 a {
    color: #9f9f9f;
  }
  

#Header {
  text-align: justify;
}
 
 #All {
     position:absolute;
     top:0px;
  border:1px solid #ccc;
     width:835px;
     padding:12px;
     background-image:none;
     background-repeat:repeat-x;
     background-color:#0C090A;
     color: #000;
   }
  
   #ContainerContent{
     width:480px;
     float:left;
     background-color:#000;
     border:1px solid #333;
     padding:12px;
     color: #98AFC7;
     
   }
  
 
.HeaderThemenNav {
