css:
@-moz-document url(https://www.peka.poznan.pl/vm/){

@font-face {
	font-family: 'Ubuntu';
	font-style: normal;
	font-weight: 400;
	src: local("Ubuntu"), url("http://themes.googleusercontent.com/static/fonts/ubuntu/v4/hHs22WBTTzMhSqc5uBBDKg.woff") format("woff");
}

body {
	padding:0px; margin:0px; border:0px;
	width: 100%;
	height: 100%;
	overflow-y: scroll;
}

div {
	 margin:0px; border:0px; font-family:Tahoma;
}

div#messages {
	display: none;
}

.header {
	top: 0px; 
	visibility: hidden;
}

.header_in {
 top:0px; 
	height: 0px; right:0px; left:0px; background-color:#0078ad; border:0px solid #009de0;
	display: none;
}

.header_vm {
	position:absolute; 
	height: 0px; 
	width: 0px; 
	height: 0px;
	display: none;
}


.footer {
	position:absolute; bottom:0px; 
	height: 0px; left:0px; right:0px;
	background-repeat:repeat-x;
	visibility: hidden;
	display: none;
}

.footer1 {
	position:absolute; bottom:0px; 
	height: 0px; left:0px; right:0px;
	background-repeat:no-repeat; background-position:center;
	visibility: hidden;
	display: none;
}

.title {
	background-color: #0078ad;
	text-align:right; 
	color:#ffffff; font-size:24px; 
	font-weight:bold; 
	text-align: center;
	padding: 5px;
	height: 30px;
}

.searchInput {
	width: 100%; 
	font-size: 20px;
	border: 0px solid black;
	margin: 10px 0px 10px 0px;
	height: 40px;
	text-indent: 10px;
}

.list {
	position:absolute; top:30px; left:20px; width:230px; background-color:white; z-index:1000;
	border:1px solid white; overflow:hidden;
}

.subListRow { 
	font-size: 20px; 
	font-weight: 700;
	/* margin:4px; */ color:gray; 
	padding: 16px 8px 16px 8px !important; text-align:left; background-color:white; cursor:pointer;
	border-bottom: 1px solid black;
	background-color: lightgray;
	color: #009de0;
}

.listRow { 
	font-size: 20px; 
	font-weight: 700;
	/* margin:4px; */ color:#009de0; 
	/* padding: 4px; */ 
	padding: 16px 8px 16px 8px;
	text-align:left; 
	background-color: lightgray; cursor:pointer;
	border-bottom: 1px solid black;
}

.listRow:hover, .subListRow:hover {
	color:#FFFFFF; background-color:#009de0;
}

.listRowHead {
	font-size: 16px;  color:#009de0; 
	padding: 8px; text-align:left; background-color:#f0f0f0
;
}

.bollardLink {
	position:absolute; display:block; top:20px; right:60px; font-size:10px; text-decoration:none; color:#FFFFFF; padding:3px;
	visibility: hidden;
}
.bollardLink:hover {
	background-color:#FFFFFF; color:#009de0;
}
.bollardSymbol {
	margin:0; padding:0; 
	font-size:18px; color:#eeeeee;
	visibility: hidden;
	height: 0px;
}



.content {
    width: 100%;
    height: 100%;
}

.content_in {
    position: relative;
    left: 0;
    right: 0;
    /* top: 353px; */
    /* bottom: 75px; */
    /* border-width: 0px 1px 0px 1px; */
    font-weight: bold;
    overflow: hidden;
    display: block;
}

.logo {
	position: absolute; top:10px; left:10px;
	visibility: hidden;
	width: 0px;
	height: 0px;
}

.search {
	/* padding: 20px; */
}


.searchRow {
    position: relative;
    width: 100%;
    margin: 10;
}

.searchRow:first-child .searchList {
	z-index: 2000 !important;
}

.searchRow:nth-child(2) .searchList {
	z-index: 1500 !important;
}

.searchRow:nth-child(3) .searchList {
	z-index: 1000 !important;
}

.searchRow:first-child .searchSublist {
	z-index: 2200 !important;
}

.searchRow:nth-child(2) .searchSublist {
	z-index: 1700 !important;
}

.searchRow:nth-child(3) .searchSublist {
	z-index: 1200 !important;
	width: 100% !important;
}

.searchTitle {
    width:140px;
    background-color:transparent;
    position:absolute;
    top:4px;
    left:0px;
    visibility: hidden;
}

.searchList {
    width: 100%;
    position: absolute;
    top: 66px;
    left: 0px;
    border: 0px solid transparent !important;
    border-top: 1px solid black !important;
}

.searchList {
    width: 100%;
    position: absolute;
    top: 66px;
    left: 0px;
    border: 0px solid transparent !important;
    border-top: 1px solid black !important;
}

.searchSublist {
    overflow: auto;
    /* min-width: 270px; */
    position: relative;
    top: 0px !important;
    border: 0px solid transparent !important;
    width: 100%;
    /* min-height: 1000px; */
}

.content_in .line {
    width: 100px;
    float: left;
    text-align: center;
}

.content_in .direction {
    overflow: auto;
}

.content_in .time {
    width: 100px;
    float: right;
    text-align: center;
}

.timesHeader {
	margin-top:20px;
    height: 30px;
    font-size: 24px;
    color: #0078ad;
    padding: 15px 5px 5px;
}

.content_in .row {
    font-size: 24px;
    padding: 5px;
}

.content_in .row .line {
    color:#005e87;
}

.content_in .row .direction {
    color:gray; overflow:hidden;
}

.blink {
	color:#0078ad;
    animation-duration: 1s;
    animation-name: blink;
    animation-iteration-count: infinite;
    animation-direction: alternate;
    animation-timing-function: ease-in-out;
    -webkit-animation-duration: 1s;
    -webkit-animation-name: blink;
    -webkit-animation-iteration-count: infinite;
    -webkit-animation-direction: alternate;
    -webkit-animation-timing-function: ease-in-out;
}
@keyframes blink {
    from {
        opacity: 1;
    }
    to {
        opacity: 0;
    }
}
@-webkit-keyframes blink {
    from {
        opacity: 1;
    }
    to {
        opacity: 0;
    }
}

.displayAll {
	font-size: 20px; background-color:gray; color:#FFF; text-align:center; 
	padding: 16px; text-transform:uppercase; cursor:pointer;
	font-weight: 700;
}

.displayAll:hover {
	background-color:#009de0; color:#FFFFFF;
}

.listRowAllBollardsHead {
	font-size:24px; margin:4px; color:#009de0; padding:5px; text-align:left; background-color:#f0f0f0
}

.searchRow:nth-child(3) .searchSublist > div{
	float: none !important;
	width: 100% !important;
}

img {
	display: none;
}
}
