css:
@-moz-document url-prefix("https://www.houzz.com/"), domain("gardenweb.com") {

/* i really want this to be global */

  
html {
    background-image:url(https://i.imgur.com/54v1Vdw.png) !important;
    background-attachment: fixed !important;
    background-size: cover !important;
}
    
body {
    height:auto !important;
    width: 88% !important;
    margin-left:  8% !important;
    margin-right:auto !important;
    background: none !important;}

#mainArea {
    width: 103% !important;}

/* main body not in boxes */
.hbs, .hbs body  {
    font-weight: 300 !important;
    font-size: 106% !important;}   

 
#questionCard #answersCtn .feedComments .comment, #questionCard #answersCtn .feedComments .addComment, #questionCard #answersCtn .feedComments .moreComments, .topic-columns, #questionDesc, .question-card, .hbs .comment, .comment.edit-comment-rte, .houzz-header    {

    background-color:#EDF0E2 ! important; }
    
    

        /* main forum page list of topics -size*/ 
.question-card.small.question-card.small {
    border: #EDF0E2 solid 2px ! important;
        width: 95%;
    margin-right: -10px;
    margin-left: -20px;} 
     
.questionTitle {
    font-size: 16px !important;
    font-weight: 800 !important;}


#questionCard #questionTitle {
    font-weight: 700 !important;    }  
    
.question-grey-bg .askQuestionSubTitle {
    color: #0F2F3B !important;}

.hbs .header-2 {
    color: #0F2F3B !important;
    font-weight: 300;
}

.hbs .header-1 {  
    font-weight: 400 !important;
    color: #0F2F3B !important;
}

#questionCard #questionInfo {
    color: #0F2F3B   ! important;}

/* original poster main question box on thread page */
#questionDesc   {

    margin: 8px !important;
         border-top: 1px solid #52416B !important;
        border-bottom: 2px solid #52416B !important;
     border-right:  6px #52416B   !important;
     border-left:  6px #52416B  !important;
       border-right-style: ridge !important;
        border-left-style: ridge!important;
   padding: 18px !important;
            width: 102% ! important;
  margin-left:    -20px     ! important   ;
}  


  /* individual comment boxes on thread page */
.hbs .comment {

    border: #A9A9A9 solid 2px;}
    
 /* main thread replies boxes*/   
 .comment.edit-comment-rte {     
          border-top: 2px solid #82A8A9 !important;
        border-bottom: 2px solid #82A8A9 !important;
     border-right:  6px  #CCD6E8 !important;
     border-left:  6px #CCD6E8   !important;
        border-right-style: ridge !important;
        border-left-style: groove!important;    
     margin-bottom: 5px !important;
      width: 100%! important;
        margin-right: -20px!important;
    margin-left: 0px!important;
 } 


/* original poster main question box on thread page */
#questionDesc   {
   background-color:  #EDF0E2 !important;
         border-top: 1px solid #C3E8D0 !important;
        border-bottom: 2px solid #C3E8D0 !important;
     border-right:  6px #C3E8D0   !important;
     border-left:  6px #C3E8D0  !important;
       border-right-style: ridge !important;
        border-left-style:groove!important;
   padding-left: 36px;
       padding-top: 26px;
            margin-right: -20px!important;
    margin-left: 0px!important;
}


.feedComments .addComment {
    background-color:  #ABD6C4;}

/* fonts for main question & all comments */
.largeComments .commentBodyText, #questionCard #questionDesc {
  font-family: "Verdana", sans-serif !important;
  font-size:115% !important;
}
    
/* fonts for typing comments */
.note-editor{
  font-family: "Verdana", sans-serif !important;
  font-size:125% !important;
}

  /*font-size for forum links on thread page*/
.navigationTopicBreadcrumbs {
  font-family: "Verdana", sans-serif !important;
  font-size: 130% !important;
        font-weight: 500 !important;} 
.hbs .header-1 {
    font-weight: 600;
}  

.hbs .text-primary {
    color:#224240 !important;}  

   
    
    /*most links in forum pages*/  
.hbs a.colorLink:link {text-decoration: underline; color:#224240 !important;}
.hbs a.colorLink:visited {color:#0F2F3B !important;}  
.hbs a.colorLink:hover {color:#40248B !important;}

/*list of links to all forums*/
a.topic-link:link {color:#224240 !important;}
a.topic-link:visited {color:#0F2F3B !important;}
a.topic-link:hover {color:#5D198B !important;}
 


.hbs .small-text {
    font-size: 130% !important ;
color: #224240 !important ;}
    


/* scroll to top button,change to show up on grey background*/

.hbs .hide {
    display: block !important;
}


#ScrollToTopContainer.hide {
    opacity: .4!important;
    height: unset !important;
    right: 0% !important;
}
    
#questionCard #questionBody, #questionCard #questionTitle, #questionCard #questionInfo, #questionCard #questionDesc, #questionCard .questionActions, #questionCard #answersCtn, .width-capri, .middleContent, .leftSideContentWithAd, .rightSideContent, container trackMe {
    width: 96% !important;}

 
.related-forums, #footerContainer, .sidebar.filter-tree.gardenWebTopicFilters.collapsible, .sidebar {
    background: none  !important;} 
    
.rightSideContent,#questionCard div#questionBody {
    float: unset !important;  
     margin-left: 24px !important;} 
    
.footer-column, ul.footer-column {width: 25% !important;}
.footer-columns {width: 100% !important;}
#footerContainer, .footerContainer { min-width: unset !important;}
    
#questionUserPhoto { margin-right: 20px !important;}    
    
/* make photo attachment icon darker on grey background */  
.question-card.small .hzi-font {color:grey !important;}
    
.container {
    width: 94%}
  
.player-wrapper { width: 100% !important;}  
    
.question-grey-bg .leftSideBar {
         margin-left: -34px  !important;
    float: left;
    width: 130px !important;
        margin-right: 0px !important;
}

    
/*fix all pages to wide feed*/
/*fix profile page to wide feed*/
/*fix main pages to wide feed*/
    
.hbs .container {
    margin-right: -10px!important;
    margin-left: -10px!important;
     width: 100% !important;}
 
.hbs p {
    word-wrap: break-word !important;
    width: 101%!important;}


/*fix profile page to wide feed*/
.rightSideContent {
    width:99%!important;}

    
    
.profile-r-sidebar, .profile-content-section {
    width: 102%!important;
    margin-right:0px!important;
    margin-left: 0px!important;}
.profile-content-wrapper {
    overflow:  visible;
    width: 100%!important;
    margin-right: 0px!important;
    margin-left: 0px!important;}
.profile-content-wide {
    width: 100%;
    float: unset!important;
    margin-right: 0px!important;
    margin-left: 0px!important;}


.feedContainer .feedItem .feedComments .commentsContainer {
    width: 98%!important;}

.tradelist {
    font-size: 110%! important;
    background:#FDFFF5 ! important;
         padding: 18px       !important;} 
     

  /* fix header/ search bar */   

#navHeader {
    width: 93% ! important ;
margin-left:  -35px   ! important ;}
 
.white-header .navbar-search-bar {
    width:initial !important;}
    
.floating-header #navHeader {
     width: 94% !important;
        margin-right: -50px !important;
        margin-left: -5px !important;}

.headerPromoBanner__small {
    display:    none    ! important;} 
    
.fullContent {
    width:unset !important;}
    
form#searchForm.navbar-search-bar{
          width: 50% !important;
     margin-left: 108px !important;
    position: relative !important;}

.t7 #questionBody, .t7 .comment-count, .t7 .feedComments {
    width:   100%!important;
    margin-left: 16px !important;}


.t8 .container.hz-carded, .t9 .container.hz-carded {
    background: none !important;} 

.t8 .container.profile-carded, .t9 .container.profile-carded {
    background:none ! important ;}
    
.t9.hbs,.t8.hbs,.t7.hbs {
	background: none ! important ;}
    

    
 .t9.hbs .hz-carded, .t8.hbs .hz-carded, .t7.hbs .hz-carded { 	background: none ! important ; } 

.rss-link {
    display:    none    !important;}
    
    div.related-product-categories{
        display: none    !important;}
  
.messageBody {
    background:#FDFFF5 ! important;
            padding: 19px !important;}   
    
div#tradeListBody.comp-box {
    width: 80%   ! important;}


    
.exchange-disclaimer {
        display: none    !important;}
    
.feedItem.medium-feed .feed-time {
    float: unset;}
    
    div.middleContent  {
    width   :  75%  !   important;
    margin-left   : 0px    !   important ;
    margin-right   : 0px    !   important ;}  
    
    
/* hides extra stuff, can add additional things */
.houzz-header-secondary.header-nav-dropdown-menu, .hzTipBox, .unstyled.header-nav.main-nav, #navFeature, #navMyCart, #navTalk, #sidebar, #wnBox, #wnButton, .bigBold, .nav-holder {display: none !important;}

#special-header-promo .sale-rectangle {display:none  ! important;}

/*.r-sidebar display:none*/
.r-sidebar .whiteCard.q .thumb {display:none  ! important;}
.r-sidebar:last-child {display:none  ! important;}  
.r-sidebar:first-child { display:none  ! important;}
.browseQuestionsModule { overflow: hidden; display:none  ! important;}
.r-sidebar {display:none  ! important;} 
.hzCompleteness .hzComletenessMessage {display: none  ! important;}
.profile-completeness {display: none  ! important;}

.top-content .advice-ctaWrapper {display: none  ! important;}
.top-content .gw-announcement {display: none  ! important;}

/*display: none .profile-l-sidebar*/  
.profile-l-sidebar .hzBtn {display: none !important;}

/*display: none .follow-box*/
.follow-section .follow-box + .follow-box {display: none !important;}
.follow-section .follow-count {display: none !important;}
.follow-section .follow-box {display: none !important;}
    
.houzz-header-secondary {  display: none !important;} 

@media only screen and (max-width: 800px), screen and (max-device-width: 800px){

     .leftSideBar {display:block !important; width:initial !important;}

     .rightSideContent {width:0% !important; margin-left:0% !important;} 

     /*remove extra stuff on all forums pages to save space */

     .welcome-box.with-claim-box {display:none !important;}

     .gw-entry-links {display:none !important;}

      /*remove extra stuff on individual forum page and make new post box wide */

     .middleContent {width:100% !important;}    

     .rightSideBar {display:none !important;}

/* hide app promo on mobile browsers that covers content */  

#signUpPromoFooter {display:none !important; }  

/*changed from 1000px so less horizontal scrolling, referenced in header and footer*/  

.container {width:98% !important;}

/*testing*/  

#mainArea #mainContent:after {

  content: "" !important;

  display: table !important;

  clear: both !important;}   

/* switch from set pixels to % */

.leftSideBar {width:20% !important; float: left !important;}

.rightSideContent {width:78% !important; float: left !important; margin-left:2% !important;} 

.questionTitle {line-height: normal;}

/* switch from set pixels to % */

/*new post area and announcement area on individual forum page */

.middleContent {width:60% !important;}    

.rightSideBar {width:38% !important;}

.topic-columns {width: 100% !important;} 

.topic-column {width: 31% !important; padding: 1% !important;}  

.topic-list, .topic-letter {width:auto !important;}

/*undo set pixel size of forum headers */
.width-capri  {width:initial !important;}  

/* switch from set pixels to % */

/* on single discussion page  */
.leftSideContentWithAd {width:68% !important; float:left !important; margin-right:2% !important;}
.adSideBar {width:30% !important; float: left !important;} 
.mobileAppAd {display:none !important;}

/* switch from set pixels to % */   

/*make footer not a fixed size */
#footerContainer, .footerContainer{
  width: 100% !important;
  min-width:100% !important;
  height:auto !important;}  

#footercontent{overflow:auto !important;}  

.footer-columns {width:auto !important;}    

/* prevent image distort in first post and comment replies*/
#questionCard #questionDesc img,
.commentBodyText img {
    width:initial !important;
    height:initial !important;
    max-width: 100% !important;
    max-height: 100% !important;
    object-fit: contain !important;}    

}    
    
/*screens smaller than 800px on comp or device move to vertical layout to save space*/


@media only screen and (max-width: 800px), screen and (max-device-width: 800px){


     .leftSideBar {display:block !important; width:initial !important;}
     .rightSideContent {width:100% !important; margin-left:0% !important;} 

     /*remove extra stuff on all forums pages to save space */
     .welcome-box.with-claim-box {display:none !important;}
     .gw-entry-links {display:none !important;}

      /*remove extra stuff on individual forum page and make new post box wide */
     .middleContent {width:100% !important;}    
     .rightSideBar {display:none !important;}
     .question-card.small .last-comment-date {position:initial !important;}
     .leftSideContentWithAd {width:100% !important; margin-right:0% !important;}
     .adSideBar {display:block !important; width:initial !important;}

     /*to line up question and comments and save space*/
     #questionCard div#questionBody {margin:0px !important;}  
     #questionCard #questionUserPhoto {float:none !important;}  
     #questionCard .topics-breadcrumb {margin-left:0px !important;}
} 


/* for tablets and smart phones to trigger in portrait mode
   force all fonts to larger set size of user choice
   correct layout bugs caused by set heights with too large of fonts */


@media only screen and (max-device-width: 800px) {   



            
     .hbs * {font-size:x-large !important;}  

     .r-sidebar .whiteCard * {max-height:initial !important;
            display: none}

     .houzz-header {height: auto !important;}

     .userImage {font-size:initial !important;}

     .rich-text-editor-wrapper .note-toolbar {height:auto !important;}
    
     .hbs .one-line {white-space:normal !important;}

      body {margin-left:5% !important; margin-right:5% !important;}
}



@media only screen and (max-device-width: 800px) and (orientation: portrait) {   


            .hbs * {font-size:x-large !important; box-sizing:border-box !important;}  

}

   
    

}
