css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.google.com") {

body {
margin-top:40px !important;

}

#gb {
border:2px dashed #D6E9FF !important;
background:white none repeat scroll 0 0 !important;
float:left !important;
position:fixed !important;
left:0 !important;
top:0 !important;
padding:4px !important;
text-align:right !important;
}


#gbar {
border:2px dashed #D6E9FF !important;
background:white none repeat scroll 0 0 !important;
padding:4px !important;
float:right !important;
position:fixed !important;
right:0 !important;
top:0 !important;
text-align:left !important;
}



#tads {
background:white !important;
color:lightgray !important;
}

#tads:hover {
background:whitesmoke !important;
color:grey !important;
}

#rhsline {
display:none !important
}

#tads a, #rhsline a, #tads cite, #rhsline cite {
color:lightgray !important;
}

#tads a:hover, #rhsline a:hover {
color:darkgrey !important;
}

.g {
background:Aliceblue none repeat scroll 0 0 !important;
background-image:url(http://img219.imageshack.us/img219
/1650/highlightgradejf6.png) !important;
background-repeat:repeat-x !important;
border:2px dashed #D6E9FF !important;
border-left:4px solid  #B3D2F7 !important;
padding:10px !important;
font-size:12px !important;
color:grey !important;
   }

.g:hover {
background:lightyellow none repeat scroll 0 0 !important;
background-image:url(http://img504.imageshack.us/img504/7815/fadeyellownz2.gif) !important;
border:2px solid #D6E9FF !important;
border-left:6px solid #85C1F6 !important;

padding:10px !important;
font-size:12px !important;

color:black !important;
}

.g a {
text-decoration:none !important;
}

.g:hover a {
text-decoration:underline !important;
}




table#nav {
position: fixed !important;
bottom: 0 !important;
width: 100% !important;
height: 16px !important;
background-color: AliceBlue !important;
background-image:url(http://img219.imageshack.us/img219
/1650/highlightgradejf6.png) !important;
background-repeat:repeat-x !important;
border-top: 1px solid black !important;
margin: 0 !important;
padding: 0 !important;
}
table#nav tr{
width: 100px !important;
}
table#nav td{
width: 8px !important;
}
table#nav span{
display: none !important;
}
table#nav *{
font-size: 9pt !important;
font-weight:bold !important;
margin: 0 !important;
padding: 0 !important;
}

}
