css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.thesecretworld.com") {
  
.postbody {
	padding-right: 3em;
	padding-top: 1em;
}
	
.postuseravatar {
    float: none !important;
		margin: 0 auto;
}
	
.bbcode_container div.bbcode_quote {
    font: italic 13px Droid;
}

.lastedited {
    font-style: normal;
	}

body {
	width:auto;
	min-width:650px;
	max-width:1000px;
	font-size:14px;
	line-height: normal !important;
}

html {
	background: url("http://forums.thesecretworld.com/images/tsw/Secret_War_Background_templars.jpg") no-repeat fixed center top #000;
}

.body_wrapper {
    padding: 6px 8px;
    background: none no-repeat scroll 0% 0% rgba(20, 20, 20, 0.9);
    border-radius: 5px;
		box-shadow: none;
}	

.newcontent_textcontrol {
    box-shadow: 0px 0px 0px #000;	
}	

.breadcrumb {
    border: 0px;
}	

.toolsmenu {
    border: 0px;
    margin: 0px 0px;
}	

.noinlinemod.below_postlist {
margin-top: 1em;
}
	
.newcontent_textcontrol {
    border-radius: 5px;
}
	
#pagetitle {
		visibility: hidden;
}
	
.postbitlegacy, .postdetails, .eventbit, .eventdetails {
    border: 0px;
}
	
.forumbit_post .forumrow, .forumbit_nopost .forumhead, .forumbit_nopost .forumrow, .forumbit_post .forumhead, .forumbits .forumhead {
	box-shadow: none;

}

.forumbit_nopost .forumhead .forumtitle, .forumbit_nopost .forumhead span, .forumbit_nopost .forumhead .collapse, .forumbit_post .forumhead h2 span, .forumbits .forumhead {
	font: bold 12px Droid,Tahoma,Calibri,Verdana,Geneva,sans-serif;
}
	
#forums .L1 .forumhead a {
	font: bold 12px Droid,Tahoma,Calibri,Verdana,Geneva,sans-serif;
}
	
.navtabs li.selected a.navtab {
    background: url("http://i.imgur.com/Z1QwU40.png") repeat-x scroll 0% 0% #999;
}
	
.navtabs li a.navtab:hover {
	background:rgb(153, 153, 153) url("http://i.imgur.com/Z1QwU40.png") repeat-x   0 -0px;
	color:rgba(20,20,20,0.9);
}
	
.threadlist, .threadbit {
	border: 0px;
	box-shadow: none;
}	
	
}
