css:
@-moz-document url-prefix("https://chamilo.hogent.be") {
body {
  font-family: "Comic Sans MS";
 	background: url('http://www.rubenvermeulen.be/projecten/file_secure_upload/uploads/CrushTheButton/bgHoGentGoatSkin.jpg'); 
  
  cursor:url(http://i4.minus.com/ibHHzcTFyzqzn.png) 15 15,move;
}
#main{
  background: url('http://i.imgur.com/PoG8yYL.png');

}

#header1 {
  	height: 150px !important;
	background-image: url('http://static.tumblr.com/a1abe0e85a5b010ab9b2d77ee785a2ae/vovl4ph/4YGmtx7r9/tumblr_static_spooky_skeletons.gif'),  url('http://www.rubenvermeulen.be/projecten/file_secure_upload/uploads/CrushTheButton/bgHeaderHoGentGoatSkin.jpg') !important;

}

#header1 div.banner {
  	top: 0 !important;
    bottom: 0 !important;
	height: 100px !important;
    margin: 0 20px !important;
}

#header1 div.banner span.logo {
 	height: 180px !important;
}

#footer {
 	background: #006401 !important;
}

.icon-logo-header {
	background: url('http://24.media.tumblr.com/8d7557674ccd702b3074d061210a121b/tumblr_mhhugtsNY71qfnmeto1_250.png') no-repeat !important;
  background-size: 180px !important;
  -webkit-animation: spin 1.5s infinite linear;

}
@-webkit-keyframes spin {
    0%   {-webkit-transform: rotate(0deg)}
    100% {-webkit-transform: rotate(359deg)}
}


div.portal_block {
  	
  	background-image: url(http://media.giphy.com/media/3EAKxhSdrpiOHe5qQE/giphy.gif)!important; 
    
}

.navbar ul li {
	background: #006401 !important;
    border-radius: 1px 1px 0 0 !important;
}
  
.navbar ul ul {
	top: 34px !important; /* level 1 li height */
  	list-style-type: square !important;
  	padding: 20px;
}
  
.navbar ul ul li {
  	min-width: 140px !important;
  list-style-type: square !important;
}
  
.navbar ul li a,
.navbar ul li ul li a {
  	display: block !important;
  	padding: 0 15px !important;
    color: #fff !important;
  	font-size: 14px !important;
  	font-weight: normal !important;
}

.navbar ul li a {
	height: 35px !important;
  	line-height: 35px !important;  
}

.navbar ul li a:hover {
	color: #151515 !important;
}
    
.navbar ul ul li a {
  	height: 32px !important;
  	line-height: 32px !important;
    background: #f2f2f2 !important;
    border: 1px solid #dadada !important;
	border-bottom: 0 !important;
	color: #151515 !important;  
}

.navbar ul ul li a:hover {
    background: #dadada !important;
}

.navbar ul ul li:first-child a {
	border-top: 0 !important;  
}

.navbar ul ul li:last-child a {
	border-bottom: 1px solid #dadada !important;  
}

.navbar ul li.current,
.navbar ul li:hover {
  	bottom: 0 !important;
  	margin-top: 0 !important;
  	background: #f2f2f2!important;
    border: 0 !important;
}

.navbar ul li a.current {
  	color: #151515 !important;
}

.navbar ul:hover li a {
	color: #151515 !important;  
}

#tab_menu {
  	height: 32px  !important;
 	padding: 0 20px  !important; 
}

#tab_menu li {
  	margin: 0  !important;
  	border: 0  !important;
  	border-radius: 0  !important;
}

#tab_menu li.normal {
  	
 	border-left: 1px solid #dadada  !important;
  	border-right: 1px solid #dadada  !important;
}

#tab_menu li.current {
 	background: #dadada  !important;
  	border: 0  !important;
  	border-left: 1px solid #dadada  !important;
  	border-right: 1px solid #dadada  !important;
}

#tab_menu li a {
 	line-height: 32px  !important; 
}

.description > div:first-of-type > img,
#user_account img {
  content: url('https://lh3.googleusercontent.com/-o2LUit25lTI/AAAAAAAAAAI/AAAAAAAAAEA/CkPuD5jGAGE/photo.jpg') !important; 
}

a {
 	color:  #006401 !important; 
}

.feed-entry img {
   	content: url('http://spinandstop.files.wordpress.com/2011/01/cartoon-goat.png');
    max-width: 16px;
  }
        
.toolblock img {
   	content: url('http://spinandstop.files.wordpress.com/2011/01/cartoon-goat.png'); 
  }
a:hover {
    position: relative;
  cursor:url(http://i4.minus.com/ibHHzcTFyzqzn.png) 15 15,move;
  
}
a:hover:after {
    content: url(https://38.media.tumblr.com/7bb7bb68678350700d5a2081938c74fa/tumblr_ncitcy5oJx1snslrwo1_500.gif); /* no need for qoutes */
    
    position: absolute;
    left: -200px; /* change this value to one that suits you */
    top: -120px; /* change this value to one that suits you */
  -moz-animation: cssAnimation 0s ease-in 1.5s forwards;
    /* Firefox */
    -webkit-animation: cssAnimation 0s ease-in 1.5s forwards;
    /* Safari and Chrome */
    -o-animation: cssAnimation 0s ease-in 1.5s forwards;
    /* Opera */
    animation: cssAnimation 0s ease-in 1.5s forwards;
    -webkit-animation-fill-mode: forwards;
    animation-fill-mode: forwards;
  pointer-events: none;
    }
@keyframes cssAnimation {
    to {
        width:0;
        height:0;
        overflow:hidden;
    }
}
@-webkit-keyframes cssAnimation {
    to {
        width:0;
        height:0;
        visibility:hidden;
    }
}
.entry-title{cursor:url(http://i4.minus.com/ibHHzcTFyzqzn.png) 15 15,move;}
.entry-title:before {
  content: "MLG ";
   -webkit-animation: blink 0.5s linear infinite;
    -moz-animation: blink 0.5s linear infinite;
    animation: blink 0.5s linear infinite;
  
}
 @keyframes blink {  
    0% { color: red; }
    100% { color: black; }
}
@-webkit-keyframes blink {
    0% { color: red; }
    100% { color: black; }
}
    .entry-title:after {
  content: " LOL!! XXDDXDD";
    -webkit-animation: blink 0.5s linear infinite;
    -moz-animation: blink 0.5s linear infinite;
    animation: blink 0.5s linear infinite;
}
img[src="https://chamilo.hogent.be/application/hogeschool_gent/integration/core/home/resources/images/aqua/sso/logo_email.png"],

img[src="https://chamilo.hogent.be/application/hogeschool_gent/integration/core/home/resources/images/aqua/sso/bamaflex.png"]
{
  content: url('https://lh3.googleusercontent.com/-o2LUit25lTI/AAAAAAAAAAI/AAAAAAAAAEA/CkPuD5jGAGE/photo.jpg') !important; 
  width: 32px;
  height: 32px;

}
}
