  .minwidth, .theme {background: url(http://ya-vidoc3000.narod.ru/anime/10.jpg) no-repeat fixed 100%;-webkit-background-size: cover; background-size: cover;}
  /*Почта на главном окне*/
  .mailbox {background-color: white !important; opacity: 0.7;}
  .mailbox:hover {opacity: 1;}
  /*Ставим почту по центру как бы xD*/
  .layout__leftcol {margin-top: 300px; margin-left: 500px;}
