  /*Прозрачная картинка на странице поиска*/
  .wallpapers, #wallpapers {opacity: 0.5; display: none !important;}  
  /*Поиск, хотя эта херня хуже яндекса*/
  .search {background-color: white !important; opacity: 0.7;}
  .search__projects-list {padding-left: 20px;}
