  .b-datalist__item__link, .b-datalist__item__link:hover, .b-datalist__item__attachment__link, .b-datalist__item__attachment__link:hover, .b-datalist__item__filename__link, .b-datalist__item__filename__link:hover, .b-datalist__item__download__link, .b-datalist__item__download__link:hover {color: red;}
  .login-page__external {margin-left: 500px; margin-top: 200px;}
  .boxGray {box-shadow: none;}
  .box-login-color2.color-on .box-login-content {background-color: white; background-image: linear-gradient(to bottom, white, white);}
.search_form .t-element, .search_form .search_form_in, .search_form_action_in {background: none; height: 80px; background-color: black !important;}
