  .search_form_action .action-block .button, .search_form_action .action-block .button:hover, .search_form_action .action-block .button:active {background-color: red;}
  .content {background-color: white !important; opacity: 0.7;}
  .rmr {background: none repeat scroll 0px 0px transparent;}
  .column_left, .column_right {display: none !important;}
  .theme-9 .bg, .bg_theme-9 {background: none !important;}
